import React, { Component } from 'react'
import {StepBackwardOutlined, StepForwardOutlined, CaretRightOutlined, PauseOutlined, FireOutlined} from '@ant-design/icons';
import { Button, Slider } from 'antd'
import PubSub from 'pubsub-js';

export default class PlayBar extends Component {
    state = {
        playList: [],
        // 当前bo'fang
        index: 0,
        paused: true,
        currentTime: 0,
        duration: 0
    }

    componentDidMount() {
        this.token = PubSub.subscribe('play', this.playMusic);
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.token);
    }

    // 收到通知，播放音乐
    playMusic = (msg, data) => {
        const playList = this.state.playList;
        for (let i = 0; i < playList.length; i++) {
            if (playList[i].musicId === data.musicId) {
                this.setState({index: i});
                return;
            }
        }
        const newPlayList = [...playList, data];
        this.setState({playList: newPlayList, index: newPlayList.length - 1, paused: false});
    }

    playBackward = () => {
        const newIndex = (this.state.index - 1 + this.state.playList.length) % this.state.playList.length;
        this.setState({index: newIndex, paused: false});
        this.audio1.play();
    }
    
    playOrPause = () => {
        this.state.paused ? this.audio1.play() : this.audio1.pause();
        this.setState((state) => {
            return {paused: !state.paused};
        });
    }

    playForward = () => {
        const newIndex = (this.state.index + 1) % this.state.playList.length;
        this.setState({index: newIndex, paused: false});
        this.audio1.play();
    }

    // 音乐加载完毕，可以播放
    canPlay = () => {
        this.setState({duration: this.audio1.duration, paused: false});
        this.audio1.play();
    }

    // audio更新state时间
    updatePlayTime = () => {
        this.setState({currentTime: this.audio1.currentTime});
        if (this.audio1.currentTime === this.audio1.duration) {
            this.playForward();
        }
    }

    // 拖动条更新时间
    changePlayTime = (event) => {
        const {value} = event.target;
        this.audio1.currentTime = value;
    }

    // 解析时间成00:00格式
    parseTime = (time) => {
        var min = Math.floor(time / 60);
        if (min < 10) {
            min = '0' + min;
        }
        var sec = Math.floor(time % 60);
        if (sec < 10) {
            sec = '0' + sec;
        }
        return min + ':' + sec;
    }

    render() {
        const {currentTime, duration} = this.state;
        return (
            <>
                <div style={{position: 'fixed', left: 0, bottom: 0, height: '60px', backgroundColor: 'black', width: '100%'}}>
                    <div style={{margin: '5px auto', width: '60%', display: 'flex'}} >
                        <Button shape='circle' style={{width: '40px', height: '40px', backgroundColor: 'black', margin: 'auto 5px'}} onClick={this.playBackward}>
                            <StepBackwardOutlined style={{color: 'white', fontSize: '30px'}} />
                        </Button>
                        <Button shape='circle' style={{width: '50px', height: '50px', backgroundColor: 'black', margin: 'auto 5px'}} onClick={this.playOrPause}>
                            {this.state.paused ? <CaretRightOutlined style={{color: 'white', fontSize: '40px'}} /> : <PauseOutlined style={{color: 'white', fontSize: '40px'}} />}
                        </Button>
                        <Button shape='circle' style={{width: '40px', height: '40px', backgroundColor: 'black', margin: 'auto 5px'}} onClick={this.playForward}>
                            <StepForwardOutlined style={{color: 'white', fontSize: '30px'}} />
                        </Button>
                        <img alt={this.state.playList.length > 0 ? this.state.playList[this.state.index].musicName : ''} src={this.state.playList.length > 0 ? this.state.playList[this.state.index].albumImg : '/favicon.ico'} style={{width: '50px', height: '50px', margin: '0 10px'}} />
                        {this.state.playList.length > 0 && 
                        <div>
                            <div style={{color: 'white'}}>
                                {this.state.playList.length > 0 ? this.state.playList[this.state.index].musicName : ''}
                            </div>
                            <div className="barAndTime" style={{marginTop: '8px', width: '700px', display: 'flex'}}>
                                <div className="bar" style={{width: '600px', height: '10px', marginTop: '7px', display: 'flex'}}>
                                    <input type="range" max={duration} value={currentTime} style={{backgroundColor: 'red', height: '10px', width: '600px'}} onChange={this.changePlayTime} />
                                    {/* <Button shape="circle" icon={<FireOutlined />} type="primary" size="small" style={{marginLeft: '-12px', marginTop: '-6px'}} /> */}
                                </div>
                                <span className="time" style={{color: 'white', marginLeft: '5px'}}>{this.parseTime(currentTime)}/{this.parseTime(duration)}</span>
                            </div>
                        </div>}
                    </div>
                </div>
                <audio src={this.state.playList.length > 0 ? this.state.playList[this.state.index].playUrl : ''} proload="auto" onCanPlay={this.canPlay} onTimeUpdate={this.updatePlayTime} ref={r => this.audio1 = r}></audio>
            </>
        )
    }
}
