import React, { Component } from 'react';
import { Button, Layout, Menu, Input, Modal, Form, Checkbox, Dropdown } from 'antd';
import {Link, Route, Switch} from 'react-router-dom';
import 'antd/dist/antd.min.css';
import './App.css';
import ArtistCat from './pages/ArtistCat';
import Artist from './pages/Artist';
import PlayBar from './components/PlayBar';
import Album from './pages/Album';
import { LockOutlined, UserOutlined, PoweroffOutlined } from '@ant-design/icons';
import axios from 'axios';
import {getToken, isLogin} from './utils/auth.js';
import MyHeader from './components/MyHeader';

const { Header, Content, Footer } = Layout;
const {Search} = Input;

export default class App extends Component {
    state = {isLoginModalVisible: false, isRegisterModalVisible: false, user: {}};



    logout = (obj) => {
        if (obj.key === 'logout') {
            localStorage.removeItem('token');
            sessionStorage.removeItem('token');
            this.setState({user: {}})
        }
    }

    userMenu = () => {
        return (
            <Menu onClick={this.logout}>
                <Menu.Item key="home" icon={<UserOutlined />}>
                    我的主页
                </Menu.Item>
                <Menu.Item key="logout" icon={<PoweroffOutlined />}>
                    退出
                </Menu.Item>
            </Menu>
        );
    }

    showLoginModal = () => {
        this.closeRegisterModal();
        this.setState({isLoginModalVisible: true});
    }

    closeLoginModal = () => {
        this.setState({isLoginModalVisible: false});
    }

    showRegisterModal = () => {
        this.closeLoginModal();
        this.setState({isRegisterModalVisible: true});
    }

    closeRegisterModal = () => {
        this.setState({isRegisterModalVisible: false});
    }

    login = (value) => {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/login',
            params: {
                username: value.username,
                password: value.password
            }
        }).then((response) => {
            if (response.data.code !== 1) {
                this.alert1.innerHTML = response.data.msg;
                this.alert1.style.display = 'block';
            } else {
                if (value.remember) {
                    localStorage.setItem('token', response.data.data.username);
                } else {
                    sessionStorage.setItem('token', response.data.data.username);
                }
                this.setState({user: response.data.data});
                this.closeLoginModal();
            }
        })
    }

    register = (value) => {
        if (value.confirm !== value.password) {
            this.alert2.innerHTML = '密码不一致';
            this.alert2.style.display = 'block';
            return;
        }
        axios({
            method: 'POST',
            url: 'http://localhost:3001/server/register',
            params: {
                username: value.username,
                password: value.password,
                nickname: value.nickname
            }
        }).then((response) => {
            if (response.data.code === 1) {
                sessionStorage.setItem('token', response.data.data.username);
                this.setState({user: response.data.data});
                this.closeRegisterModal();
            } else {
                this.alert2.innerHTML = response.data.msg;
                this.alert2.style.display = 'block';
            }
        })
    }

    render() {
        return (
            <>
                <Layout className="layout" style={{minHeight: '100vh'}}>
                    <MyHeader />
                    <Content style={{ width: '1000px', margin: '0 auto' }}>
                        <div className="site-layout-content">
                            <Switch>
                                <Route path="/artist/:artistId" component={Artist} />
                                <Route path="/artist_list" component={ArtistCat} />
                                <Route path="/album/:albumId" component={Album} />
                            </Switch>
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center', height: '150px' }}>718音乐 ©2021 Created by YWJ</Footer>
                </Layout>
                <PlayBar />
            </>
        );
    }
}
