import React, { Component } from 'react';
import { Layout, Menu } from 'antd';
import {Link, Route, Switch} from 'react-router-dom';
import 'antd/dist/antd.min.css';
import './App.css';
import ArtistCat from './pages/ArtistCat';
import Artist from './pages/Artist';
const { Header, Content, Footer } = Layout;

export default class App extends Component {
    render() {
        return (
            <Layout className="layout" style={{minHeight: '100vh'}}>
                <Header style={{display: 'flex'}}>
                    <div className="logo" style={{color: 'white', marginLeft: 300}}>
                        <span className="fa fa-music"></span>&nbsp;
                        718音乐
                    </div>
                    <Menu theme="dark" mode="horizontal" defaultSelectedKeys="recommend" style={{marginLeft: 100}}>
                        <Menu.Item key="recommend">推荐</Menu.Item>
                        <Menu.Item key="artist">
                            <Link to="/artist">歌手</Link>
                        </Menu.Item>
                    </Menu>
                </Header>
                <Content style={{ padding: '0 400px' }}>
                    <div className="site-layout-content">
                        <Switch>
                            <Route path="/artist/:artistId" component={Artist} />
                            <Route path="/artist" component={ArtistCat} />
                        </Switch>
                    </div>
                </Content>
                <Footer style={{ textAlign: 'center' }}>718音乐 ©2021 Created by YWJ</Footer>
            </Layout>
        );
    }
}
