import React, { Component } from 'react';
import { Layout, Input } from 'antd';
import {Link, Route, Switch} from 'react-router-dom';
import 'antd/dist/antd.min.css';
import './App.css';
import ArtistCat from './pages/ArtistCat';
import Artist from './pages/Artist';
import PlayBar from './components/PlayBar';
import Album from './pages/Album';
import { LockOutlined, UserOutlined, PoweroffOutlined } from '@ant-design/icons';
import axios from 'axios';
import {getToken, isLogin} from './utils/auth.js';
import MyHeader from './components/MyHeader';

const { Header, Content, Footer } = Layout;
const {Search} = Input;

export default class App extends Component {






    render() {
        return (
            <>
                <Layout className="layout" style={{minHeight: '100vh'}}>
                    <MyHeader />
                    <Content style={{ width: '1000px', margin: '0 auto' }}>
                        <div className="site-layout-content">
                            <Switch>
                                <Route path="/artist/:artistId" component={Artist} />
                                <Route path="/artist_list" component={ArtistCat} />
                                <Route path="/album/:albumId" component={Album} />
                            </Switch>
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center', height: '150px' }}>718音乐 ©2021 Created by YWJ</Footer>
                </Layout>
                <PlayBar />
            </>
        );
    }
}
