import { Layout, Menu, Row, Col, Card } from 'antd'
import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';
import { removeToken } from '../../utils/auth';
import { SERVER_URL } from '../../utils/constant';

const { Content, Sider } = Layout;
const {SubMenu} = Menu;
const {Meta} = Card;

export default function ArtistList(props) {
    const [data, setData] = useState([]);
    const [selectedKeys, setSelectedKeys] = useState([])
    useEffect(() => {
        console.log(props);
        const {artistLocation, artistType} = props.match.params;
        if (artistLocation === 'hot') {
            axios({
                method: 'GET',
                url: SERVER_URL + 'artist_list/hot',
            }).then((response) => {
                console.log(response);
                if (response.data.status === 2) {
                    removeToken();
                    props.history.push('/');
                } else if (response.data.status === 1) {
                    let i = 0;
                    let newData = [];
                    while (i < response.data.data.length) {
                        newData.push(response.data.data.slice(i, Math.min(i + 4, response.data.data.length)));  //拆成二维数组，为了下面排版
                        i += 4;
                    }
                    setData(newData);
                }
            });
        } else {
            axios({
                method: 'GET',
                url: SERVER_URL + 'artist_list/' + artistLocation + '/' + artistType,
            }).then((response) => {
                if (response.data.status === 2) {
                    removeToken();
                    props.history.push('/');
                } else if (response.data.status === 1) {
                    let i = 0;
                    let newData = [];
                    while (i < response.data.data.length) {
                        newData.push(response.data.data.slice(i, Math.min(i + 4, response.data.data.length)));  //拆成二维数组，为了下面排版
                        i += 4;
                    }
                    setData(newData);
                }
            });
        }
    }, [props])

    const handleClick = (obj) => {
        props.history.push('/artist_list/' + obj.key);
    }

    return (
        <Layout>
            <Sider style={{width: 250}}>
                <Menu mode="inline" defaultSelectedKeys="hot" style={{height: '100%'}} onClick={handleClick} defaultOpenKeys={['0', '1', '2']}>
                    <Menu.Item key="hot">热门歌手</Menu.Item>
                    <SubMenu key="0" title="华语">
                        <Menu.Item key="0/0">华语男歌手</Menu.Item>
                        <Menu.Item key="0/1">华语女歌手</Menu.Item>
                        <Menu.Item key="0/2">华语组合/乐队</Menu.Item>
                    </SubMenu>
                    <SubMenu key="1" title="欧美">
                        <Menu.Item key="1/0">欧美男歌手</Menu.Item>
                        <Menu.Item key="1/1">欧美女歌手</Menu.Item>
                        <Menu.Item key="1/2">欧美组合/乐队</Menu.Item>
                    </SubMenu>
                    <SubMenu key="2" title="日韩">
                        <Menu.Item key="2/0">日韩男歌手</Menu.Item>
                        <Menu.Item key="2/1">日韩女歌手</Menu.Item>
                        <Menu.Item key="2/2">日韩组合/乐队</Menu.Item>
                    </SubMenu>
                </Menu>
            </Sider>
            <Content style={{ paddingLeft: '10px', minHeight: 280}}>
                {data.map((artistRow, index) => {
                    return (<Row gutter={[10, 5]} key={index}>
                        {artistRow.map((artist, index) => {
                            return (<Col span={6} key={index}>
                                <Link to={`/artist/${artist.artistId}`}>
                                        <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                                <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                        </Card>
                                </Link>
                            </Col>)
                        })}
                    </Row>);
                })}
            </Content>
        </Layout>
    );
}
