import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';
import { Image, Button, Table, Card } from 'antd';
import { SERVER_URL } from '../../../utils/constant';
import { playMusic } from '../../../utils/play';
import { getToken, removeToken } from '../../../utils/auth';

const collectionListColomns = [
    {
        key: 'index',
        render: (text, record, index) => {
            return index + 1;
        }
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        title: '时长',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
];

export default function CollectionList(props) {
    const [collectionList, setCollectionList] = useState({});

    useEffect(() => {
        axios({
            method: 'GET',
            url: SERVER_URL + 'collection_list/' + props.match.params.collection_list_id,
            headers: {
            Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
                props.history.push('/');
            } else if (response.data.status === 1) {
                setCollectionList(response.data.data);
            }
        });  // eslint-disable-next-line
    }, [props]);

    return (
        <>
            <div style={{display: 'flex', height: '200px', backgroundColor: 'white'}}>
                <Image alt={collectionList.collectionListName} src={collectionList.collectionListImg} style={{margin: '0 10px'}}/>
                <Card title={collectionList.collectionListName} style={{width: '100%'}} bordered={false}>
                {collectionList.createDate} 创建
                </Card>
            </div>
            <Table columns={collectionListColomns} dataSource={collectionList.musicVOList} rowKey={record => record.musicId} />
        </>
    );
}
