import React, {useEffect, useRef, useState} from 'react';
import {Carousel, Col, Row} from "antd";
import {LeftOutlined, RightOutlined} from "@ant-design/icons";
import axios from "axios";

const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
};

export default function Discover() {
    const songList = useState();

    const c1 = useRef();

    useEffect(() => {
        axios({
            method: 'GET',
            
        })
    });

    const prev1 = () => {
        c1.current.prev();
    }

    const next1 = () => {
        c1.current.next();
    }

    return (
        <>
            <div>
                <h1 style={{textAlign: 'center'}}>新歌首发</h1>
                <Carousel ref={c1}>
                    <div>
                        <Row>
                            <Col>
                                1
                            </Col>
                        </Row>
                    </div>
                    <div>
                        <h3 style={contentStyle}>2</h3>
                    </div>
                    <div>
                        <h3 style={contentStyle}>3</h3>
                    </div>
                    <div>
                        <h3 style={contentStyle}>4</h3>
                    </div>
                </Carousel>
                <LeftOutlined onClick={prev1} style={{fontSize: '20px', position: 'relative', left: '-20px', top: '-100px'}} />
                <RightOutlined onClick={next1} style={{fontSize: '20px', position: 'relative', left: '930px', top: '-100px'}} />
            </div>
        </>
    );
}
