import React, { Component } from 'react'
import axios from 'axios';
import { Card, Descriptions } from 'antd';

export default class Artist extends Component {
    state = {data: []};

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/artist',
            params: {
                artistId: this.props.match.params.artistId
            }
        }).then((response) => {
            this.setState({data: response.data});
        });
    }

    render() {
        return (
            <div>
                <Card style={{display}} >
                    <Descriptions title={this.state.data.artistName}>
                        <Descriptions.Item>{this.state.data.artistDescription}</Descriptions.Item>
                    </Descriptions>
                </Card>
                
            </div>
        )
    }
}
