import {observable, action, makeObservable} from 'mobx';

export class PlayBarStore {
    playList = [];

    index = 0;

    constructor() {
        makeObservable(this, {
            playList: observable,
            index: observable,
            setPlayList: action,
            setIndex: action
        })
    }
    setPlayList = (newPlayList) => {
        this.playList = newPlayList;
    }

    setIndex = (newIndex) => {
        this.index = newIndex;
    }
}
