import React, {useRef} from 'react';
import {Card, Carousel} from "antd";
import {ArrowLeftOutlined, ArrowRightOutlined} from "@ant-design/icons";

const contentStyle = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
};

export default function Discover() {
    const c1 = useRef();

    const prev1 = () => {
        c1.current.prev();
    }

    const next1 = () => {
        c1.current.next();
    }

    return (
        <>
            <div>
                <Carousel ref={c1}>
                    <div>
                        <h3 style={contentStyle}>1</h3>
                    </div>
                    <div>
                        <h3 style={contentStyle}>2</h3>
                    </div>
                    <div>
                        <h3 style={contentStyle}>3</h3>
                    </div>
                    <div>
                        <h3 style={contentStyle}>4</h3>
                    </div>
                </Carousel>
                <ArrowLeftOutlined onClick={prev1} />
                <ArrowRightOutlined onClick={next1} />
            </div>
        </>
    );
}
