import axios from "axios";
import PubSub from "pubsub-js";

export function playMusic(record) {
    axios({
        method: 'GET',
        url: 'http://localhost:3001/kugou',
        // headers: {
        //     cookie: 'kg_mid=39f0247247ae8d32fa08e059894d0361; kg_dfid=3x8KjG0Z8M2U2M53fL2FxHb9; kg_dfid_collect=d41d8cd98f00b204e9800998ecf8427e; Hm_lvt_aedee6983d4cfc62f509129360d6bb3d=1638190010,1638235596,1638407827,1638525137; KuGoo=KugooID=1534449652&KugooPwd=490DE731557209844A6F65DBD92B3AF1&NickName=%u6587%u5144%u0079%u0077%u006a&Pic=http://imge.kugou.com/kugouicon/165/20190817/20190817235035526577.jpg&RegState=1&RegFrom=&t=5807e5ff2d7eaedd7a78dcb0a15c65ffe0ec67a5de90b788af92755a0970f503&t_ts=1638525543&t_key=3PYU2UXF4WX5XOKDT3BLFOZHZYSH3XER&a_id=1014&ct=1638525543&UserName=%u0031%u0033%u0038%u0035%u0037%u0038%u0036%u0030%u0032%u0033%u0034; KugooID=1534449652; t=5807e5ff2d7eaedd7a78dcb0a15c65ffe0ec67a5de90b788af92755a0970f503; a_id=1014; UserName=%u0031%u0033%u0038%u0035%u0037%u0038%u0036%u0030%u0032%u0033%u0034; mid=39f0247247ae8d32fa08e059894d0361; dfid=3x8KjG0Z8M2U2M53fL2FxHb9; kg_mid_temp=39f0247247ae8d32fa08e059894d0361;'
        // },
        params: {
            r: 'play/getdata',
            hash: record.musicId,
            album_id: record.albumId,
            mid: '39f0247247ae8d32fa08e059894d0361'
        },
        withCredentials: true
    }).then((response) => {
        record.playUrl = response.data.data.play_url;
        PubSub.publish('play', record);  // record是包含url的MusicVO
    });
}
