import React, { Component } from 'react'
import {PlayCircleOutlined, StepForwardOutlined, StepBackwardOutlined} from '@ant-design/icons'

export default class PlayBar extends Component {
    state = {
        playList: [],
        index: 0
    }

    render() {
        return (
            <div style={{position: 'fixed', left: 0, bottom: 0, height: '60px', backgroundColor: 'black', width: '100%'}}>
                <div style={{margin: '0 auto', width: '60%'}} >
                    <StepBackwardOutlined style={{color: 'white', fontSize: '100px'}} />
                    <PlayCircleOutlined style={{color: 'white'}} />
                    <StepForwardOutlined style={{color: 'white'}} />
                </div>
            </div>
        )
    }
}
