import React, { Component } from 'react'
import axios from 'axios';
import { Card, Layout, Typography, Menu, Table, Button, Row, Col } from 'antd';
import PubSub from 'pubsub-js';
import {Link} from 'react-router-dom';

const { Paragraph } = Typography;
const {Content} = Layout;
const {Meta} = Card;

const colomns = [
    {
        key: 'index',
        render: (text,record,index) => {return index + 1;}
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲标题',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                axios({
                    method: 'GET',
                    url: 'http://localhost:3001/kugou',
                    params: {
                        r: 'play/getdata',
                        hash: record.musicId,
                        album_id: record.albumId,
                        mid: 1
                    }
                }).then((response) => {
                    record.playUrl = response.data.data.play_url;
                    PubSub.publish('play', record);  // record是包含url的MusicVO
                })
                // console.log(text);
                // console.log(record);
                // console.log(index);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                axios({
                    method: 'GET',
                    url: 'http://localhost:3001/kugou',
                    params: {
                        r: 'play/getdata',
                        hash: record.musicId,
                        album_id: record.albumId,
                        mid: 1
                    }
                }).then((response) => {
                    record.playUrl = response.data.data.play_url;
                    PubSub.publish('play', record);  // record是包含url的MusicVO
                })
                // console.log(text);
                // console.log(record);
                // console.log(index);
            }}>{text}</Button>);
        },
        width: '400px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        title: '时长',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
];

export default class Artist extends Component {
    state = {musicData: {}, albumData: {}, type: "music", updated: false};

    componentDidMount() {
        this.getMusic();
    }

    getMusic = () => {
        this.setState({updated: false});
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist',
            params: {
                artistId: this.props.match.params.artistId
            }
        }).then((response) => {
            this.setState({musicData: response.data}, () => {
                this.setState({updated: true});
            });
        });
    }

    getAlbum = () => {
        this.setState({updated: false});
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist/album',
            params: {
                artistId: this.props.match.params.artistId
            }
        }).then((response) => {
            this.setState({albumData: response.data}, () => {
                this.setState({updated: true});
            });
        });
    }

    getData = (obj) => {
        if (obj.key === 'music') {
            if (JSON.stringify(this.state.musicData) === '{}') {
                this.getMusic();
            }
            this.setState({type: 'music'});
        } else {
            if (JSON.stringify(this.state.albumData) === '{}') {
                this.getAlbum();
            }
            this.setState({type: 'album'});
        }
    }

    render() {
        const {musicData, albumData, type, updated} = this.state;
        return (
            <>
                <div style={{display: 'flex', height: '200px'}}>
                    <img alt={musicData.artistName} src={musicData.artistImg} />
                    <Card title={musicData.artistName} >
                        <div style={{overflow: 'auto'}}>
                            <Paragraph ellipsis={{rows: 3, expandable: true, suffix: '...', symbol: '展开'}}>
                                {musicData.artistDescription}
                            </Paragraph>
                        </div>
                        
                    </Card>
                </div>
                <Content style={{ padding: '10px', minHeight: 280}}>
                    <Menu mode="horizontal" defaultSelectedKeys="music" onClick={this.getData}>
                        <Menu.Item key="music">
                            单曲
                        </Menu.Item>
                        <Menu.Item key="album">
                            专辑
                        </Menu.Item>
                    </Menu>
                    {type === 'music' ? 
                    updated && <Table columns={colomns} dataSource={this.state.musicData.musicVOList} showHeader={false} pagination={{defaultPageSize: 20}} rowKey="musicId" />
                    : updated && <Row gutter={10}>
                        {albumData.artistAlbumVOList.map((album) => {
                            return (<Col span={6}>
                                <Link to={`/album/${album.albumId}`}>
                                    <Card hoverable cover={<img alt={album.albumName} src={album.albumImg} />}>
                                            <Meta title={album.albumName} style={{textAlign: 'center'}} description={album.albumPublishTime} />
                                    </Card>
                                </Link>
                            </Col>)
                        })}
                    </Row>}
                </Content>
            </>
        )
    }
}
