import React, {useEffect, useRef, useState} from 'react';
import {Carousel, Col, Image, Row} from "antd";
import {LeftOutlined, RightOutlined} from "@ant-design/icons";
import axios from "axios";
import {SERVER_URL} from "../../utils/constant";
import './index.css';

export default function Discover() {
    const [musicList, setMusicList] = useState([]);

    const c1 = useRef();

    useEffect(() => {
        axios({
            method: 'GET',
            url: SERVER_URL + 'latestMusic'
        }).then((response) => {
            setMusicList(response.data.data);
        })
    }, []);

    const prev1 = () => {
        c1.current.prev();
    }

    const next1 = () => {
        c1.current.next();
    }

    return (
        <>
            <div>
                <h1 style={{textAlign: 'center', marginBottom: '20px'}}>新歌首发</h1>
                <Carousel ref={c1}>
                    <div style={{backGround}}>
                        <ul>
                            {musicList.map((music, index) => {
                                if (index < 9) {
                                    return (
                                        <li>
                                            <div style={{height: '70px', display: 'flex', width: '300px'}}>
                                                <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                                <div style={{height: '70px'}}>
                                                    <div style={{height: '30px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                                    <div style={{height: '30px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                                </div>
                                            </div>
                                        </li>
                                    );
                                } else {
                                    return <></>;
                                }
                            })}
                        </ul>
                    </div>
                    <div>
                        <ul>
                            {musicList.map((music, index) => {
                                if (9 <= index < 18) {
                                    return (<li>
                                        <div style={{height: '70px', display: 'flex', width: '300px'}}>
                                            <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                            <div style={{height: '70px'}}>
                                                <div style={{height: '30px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                                <div style={{height: '30px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                            </div>
                                        </div>
                                    </li>);
                                } else {
                                    return <></>;
                                }
                            })}
                        </ul>
                    </div>
                    <div>
                        <ul>
                            {musicList.map((music, index) => {
                                if (18 <= index < 27) {
                                    return (<li>
                                        <div style={{height: '70px', display: 'flex', width: '300px'}}>
                                            <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                            <div style={{height: '70px'}}>
                                                <div style={{height: '30px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                                <div style={{height: '30px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                            </div>
                                        </div>
                                    </li>);
                                } else {
                                    return <></>;
                                }
                            })}
                        </ul>
                    </div>
                    <div>
                        <ul>
                            {musicList.map((music, index) => {
                                if (27 <= index < 36) {
                                    return (<li>
                                        <div style={{height: '70px', display: 'flex', width: '300px'}}>
                                            <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                            <div style={{height: '70px'}}>
                                                <div style={{height: '30px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                                <div style={{height: '30px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                            </div>
                                        </div>
                                    </li>);
                                } else {
                                    return <></>;
                                }
                            })}
                        </ul>
                    </div>
                    <div>
                        <ul>
                            {musicList.map((music, index) => {
                                if (36 <= index < 45) {
                                    return (<li>
                                        <div style={{height: '70px', display: 'flex', width: '300px'}}>
                                            <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                            <div style={{height: '70px'}}>
                                                <div style={{height: '30px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                                <div style={{height: '30px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                            </div>
                                        </div>
                                    </li>);
                                } else {
                                    return <></>;
                                }
                            })}
                        </ul>
                    </div>
                </Carousel>
                <LeftOutlined onClick={prev1} style={{fontSize: '20px', position: 'relative', left: '-20px', top: '-100px'}} />
                <RightOutlined onClick={next1} style={{fontSize: '20px', position: 'relative', left: '930px', top: '-100px'}} />
            </div>
        </>
    );
}
