import { Layout, Menu, Row, Col, Card } from 'antd'
import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';

const { Content, Sider } = Layout;
const {SubMenu} = Menu;
const {Meta} = Card;

export default class ArtistCat extends Component {
    state = {data: []}

    componentDidMount() {
        const {search} = this.props.location; 
        const searchParams = new URLSearchParams(search.substring(1));
        const artistType = searchParams.get('artistType');
        const artistLocation = searchParams.get('artistLocation');
        if (artistType === 'hot') {
            let menuItem = document.getElementById('hot');
            menuItem.click();
        }
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist_list',
            params: {
                artistType: artistType,
                artistLocation: artistLocation
            }
        }).then((response) => {
            let i = 0;
            var newData = [];
            while (i < response.data.length) {
                newData.push(response.data.slice(i, Math.min(i + 4, response.data.length)));  //拆成二维数组，为了下面排版
                i += 4;
            }
            this.setState({data: newData});
        });
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
        const {search} = nextProps.location;
        const searchParams = new URLSearchParams(search.substring(1));
        const artistType = searchParams.get('artistType');
        const artistLocation = searchParams.get('artistLocation');
        if (artistType === 'hot') {
            let menuItem = document.getElementById('hot');
            menuItem.click();
        }
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist_list',
            params: {
                artistType: artistType,
                artistLocation: artistLocation
            }
        }).then((response) => {
            let i = 0;
            var newData = [];
            while (i < response.data.length) {
                newData.push(response.data.slice(i, Math.min(i + 4, response.data.length)));  //拆成二维数组，为了下面排版
                i += 4;
            }
            this.setState({data: newData});
        });
    }
    
    // 在卸载前取消所有异步操作和所有订阅，否则会报错
    componentWillUnmount() {
        const CancelToken = axios.CancelToken;
        const source = CancelToken.source();

        axios.get('http://localhost:3001/server/artist_list', {
            cancelToken: source.token
        }).catch(function (thrown) {
            if (axios.isCancel(thrown)) {
                console.log('Request canceled', thrown.message);
            } else {
                // handle error
            }
        });
        source.cancel('Operation canceled by the user.');
    }

    render() {
        return (
            <Layout>
                <Sider style={{width: 250}}>
                    <Menu mode="inline" defaultSelectedKeys="hot" style={{height: '100%'}}>
                        <Menu.Item key="hot" id="hot">
                            <Link to="/artist_list?artistLocation=hot&artistType=hot">热门歌手</Link>
                        </Menu.Item>
                        <SubMenu key="0" title="华语">
                            <Menu.Item key="00">
                                <Link to="/artist_list?artistLocation=0&artistType=0">华语男歌手</Link>
                            </Menu.Item>
                            <Menu.Item key="01">
                                <Link to="/artist_list?artistLocation=0&artistType=1">华语女歌手</Link>
                            </Menu.Item>
                            <Menu.Item key="02">
                                <Link to="/artist_list?artistLocation=0&artistType=2">华语组合/乐队</Link>
                            </Menu.Item>
                        </SubMenu>
                        <SubMenu key="1" title="欧美">
                            <Menu.Item key="10">
                                <Link to="/artist_list?artistLocation=1&artistType=0">欧美男歌手</Link>
                            </Menu.Item>
                            <Menu.Item key="11">
                                <Link to="/artist_list?artistLocation=1&artistType=1">欧美女歌手</Link>
                            </Menu.Item>
                            <Menu.Item key="12">
                                <Link to="/artist_list?artistLocation=1&artistType=2">欧美组合/乐队</Link>
                            </Menu.Item>
                        </SubMenu>
                        <SubMenu key="2" title="日韩">
                            <Menu.Item key="20">
                                <Link to="/artist_list?artistLocation=2&artistType=0">日韩男歌手</Link>
                            </Menu.Item>
                            <Menu.Item key="21">
                                <Link to="/artist_list?artistLocation=2&artistType=1">日韩女歌手</Link>
                            </Menu.Item>
                            <Menu.Item key="22">
                                <Link to="/artist_list?artistLocation=2&artistType=2">日韩组合/乐队</Link>
                            </Menu.Item>
                        </SubMenu>
                    </Menu>
                </Sider>
                <Content style={{ padding: '0', minHeight: 280}}>
                    {/* <Route path="/artist_list" component={ArtistList} /> */}
                    {this.state.data.map((artistRow) => {
                        return (<Row gutter={[10, 5]}>
                            {artistRow.map((artist) => {
                                return (<Col span={6}>
                                    <Link to={`/artist/${artist.artistId}`}>
                                            <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                                    <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                            </Card>
                                    </Link>
                                </Col>)
                            })}
                        </Row>);
                    })}
                </Content>
            </Layout>
        )
    }
}
