import React, {useEffect, useRef, useState} from 'react';
import {Button, Layout, Menu, Table} from "antd";
import {Link} from "react-router-dom";
import axios from "axios";
import {playBarStore} from "../../stores/playBarStore";
import {SERVER_URL} from "../../utils/constant";

const { Content, Sider } = Layout;
const {SubMenu} = Menu;

const colomns = [
    {
        key: 'index',
        render: (text, record, index) => {
            return index + 1;
        },
        width: '50px'
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        ellipsis: true,
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playBarStore.addMusic(record);
            }} style={{padding: '0'}}>
                <img alt={text} src={record.albumImg} style={{height: '100px', width: '100px'}} />
                {text}
            </Button>);
        },
        width: '300px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        ellipsis: true,
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '150px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        ellipsis: true,
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '150px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        title: '时长',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
]

export default function TopList(props) {
    const [data, setData] = useState([]);
    const [selectedKeys, setSelectedKeys] = useState('type=total');

    const title = useRef();

    useEffect(() => {
        const params = new URLSearchParams(props.location.search);
        const type = params.get('type');
        axios({
            method: 'GET',
            url: SERVER_URL + 'topList',
            params: {
                type: type
            }
        }).then((response) => {
            if (response.data.status === 200) {
                setData(response.data.data);
                setSelectedKeys(`type=${type}`);
            } else {
                props.history.push('/error');
            }
        })
    }, [props]);

    const handleClick = (obj) => {
        console.log(obj);
        props.history.push(`/topList?${obj.key}`);
    }

    return (
        <Layout>
            <Sider style={{width: 250}}>
                <Menu mode="inline" defaultSelectedKeys="type=total" style={{height: '100%'}} onClick={handleClick} defaultOpenKeys={['0', '1', '2']} selectedKeys={selectedKeys}>
                    <Menu.Item key="type=total">总榜</Menu.Item>
                    <Menu.Item key="type=0">华语榜</Menu.Item>
                    <Menu.Item key="type=1">欧美榜</Menu.Item>
                    <Menu.Item key="type=2">日韩榜</Menu.Item>
                </Menu>
            </Sider>
            <Content style={{ paddingLeft: '10px', minHeight: 280}}>
                <div style={{fontWeight: 'bold', fontSize: '20px'}} ref={title}></div>
                <Table dataSource={data} columns={colomns} rowKey={record => record.musicId} />
            </Content>
        </Layout>
    );
}
