import React, { Component } from 'react'
import { Layout, Menu, Row, Col, Card, Image } from 'antd'
import {Link} from 'react-router-dom'
import axios from 'axios';
import { getToken, removeToken } from '../../utils/auth';

const { Content, Sider } = Layout;
const {SubMenu} = Menu;
const {Meta} = Card;

export default class MyMusic extends Component {
    state = {collectionList: [], rankList: []}

    componentDidMount() {
        const word = this.props.match.params;
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/user/music',
            headers: {
                Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
            } else if (response.data.status === 1) {
                this.se
            }
        })
    }

    render() {
        return (
            <Layout>
                <Sider style={{width: 250}}>
                    <Menu mode="inline" defaultSelectedKeys="rank" style={{height: '100%'}}>
                        <Menu.Item key="rank">
                            <Link to="/my_music/rank">听歌排行</Link>
                        </Menu.Item>
                        <SubMenu key="collection_list" title="创建的歌单">
                            {this.state.collectionList.map((list) => {
                                return (<Menu.Item key={list.collectionListId}>
                                    <Link to={`/my_music/collection_list/${list.collectionListId}`}>
                                        <div style={{display: 'flex'}}>
                                            <Image width="50px" src={list.collectionListImg} preview={false} />
                                            <div>{list.collectionListName}</div>
                                            <div>{list.collectionListLength}首</div>
                                        </div>
                                    </Link>
                                </Menu.Item>);
                            })}
                        </SubMenu>
                        
                    </Menu>
                </Sider>
                <Content style={{ padding: '0', minHeight: 280}}>
                    {/* <Route path="/artist_list" component={ArtistList} /> */}
                    {this.state.data.map((artistRow, index) => {
                        return (<Row gutter={[10, 5]} key={index}>
                            {artistRow.map((artist, index) => {
                                return (<Col span={6} key={index}>
                                    <Link to={`/artist/${artist.artistId}`}>
                                            <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                                    <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                            </Card>
                                    </Link>
                                </Col>)
                            })}
                        </Row>);
                    })}
                </Content>
            </Layout>
        )
    }
}
