import React, { Component } from 'react'
import axios from 'axios';
import { Card, Layout, Typography, Menu, Table } from 'antd';
import { Link } from 'react-router-dom';

const { Paragraph } = Typography;
const {Content} = Layout

const colomns = [
    {
        render: (text,record,index) => {return index;}
    },
    {
        dataIndex: 'musicName',
        render: (text) => {return (<a onClick={Artist.playMusic}>{text}</a>);}
    },
    {
        dataIndex: 'timeLength',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = text / 60;
            if (min < 10) {
                min += '0';
            }
            var sec = text % 60;
            return (<span>{min + ':' + sec}</span>);
        }
    },
    {
        dataIndex: 'albumName',
        render: (text) => {return (<Link>{text}</Link>);}
    }
];

export default class Artist extends Component {
    state = {data: []};

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/artist',
            params: {
                artistId: this.props.match.params.artistId
            }
        }).then((response) => {
            this.setState({data: response.data});
        });
    }

    playMusic = () => {

    }

    render() {
        const {data} = this.state;
        return (
            <div>
                <div style={{display: 'flex', height: '200px'}}>
                    <img alt={data.artistName} src={data.artistImg} />
                    <Card title={data.artistName} style={{overflow: 'auto'}}>
                        <Paragraph ellipsis={{rows: 3, expandable: true, suffix: '...', symbol: '展开'}}>
                            {data.artistDescription}
                        </Paragraph>
                    </Card>
                </div>
                <Content style={{ padding: '10px', minHeight: 280}}>
                    <Menu mode="horizontal" defaultSelectedKeys="music">
                        <Menu.Item key="music">
                            单曲
                        </Menu.Item>
                        <Menu.Item key="album">
                            专辑
                        </Menu.Item>
                    </Menu>
                    <Table columns={colomns} dataSource={this.state.data.musicVOList} />
                </Content>
                
            </div>
        )
    }
}
