import axios from 'axios';
import React, { Component } from 'react'
import {Menu, Table, Button, Row, Col, Card} from 'antd';
import { Link } from 'react-router-dom';
import { playMusic } from '../../utils/play';

const {Meta} = Card;
const colomns = [
    {
        key: 'index',
        render: (text,record,index) => {return index + 1;}
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        title: '时长',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
];

export default class SearchResult extends Component {
    state = {data: [], word: ''}

    componentDidMount() {
        const {search} = this.props.location;
        const searchParams = new URLSearchParams(search.substring(1));
        const word = searchParams.get('word');
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/search',
            params: {
                word: word,
                type: 'music'
            }
        }).then((response) => {
            console.log(response);
            if (response.data.status === 1) {
                this.setState({data: response.data.data});
                this.setState({word: word});
            }
        });
    }

    handleClick = (obj) => {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/search',
            params: {
                word: this.state.word,
                type: obj.key
            }
        }).then((response) => {
            if (response.data.status === 1) {
                if (obj.key === 'artist') {
                    let i = 0;
                    var newData = [];
                    while (i < response.data.length) {
                        newData.push(response.data.slice(i, Math.min(i + 6, response.data.length)));  //拆成二维数组，为了下面排版
                        i += 6;
                    }
                    this.setState({data: newData});
                }
                this.setState({data: response.data.data});
            }
        })
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
        const {search} = this.props.location;
        const searchParams = new URLSearchParams(search.substring(1));
        const word = searchParams.get('word');
        if (artistType === 'hot') {
            let menuItem = document.getElementById('hot');
            menuItem.click();
        }
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist_list',
            params: {
                artistType: artistType,
                artistLocation: artistLocation
            }
        }).then((response) => {
            let i = 0;
            var newData = [];
            while (i < response.data.length) {
                newData.push(response.data.slice(i, Math.min(i + 4, response.data.length)));  //拆成二维数组，为了下面排版
                i += 4;
            }
            this.setState({data: newData});
        });
    }

    render() {
        const {data, word} = this.state;
        return (
            <>
                <Menu onClick={this.handleClick} mode="horizontal" defaultSelectedKeys="music">
                    <Menu.Item key="music">
                        单曲
                    </Menu.Item>
                    <Menu.Item key="artist">
                        歌手
                    </Menu.Item>
                    <Menu.Item key="album">
                        专辑
                    </Menu.Item>
                </Menu>
                {word === 'music' && <Table dataSource={data} columns={colomns} pagination={{defaultPageSize: 20}} />}
                {word === 'artist' && this.state.data.map((artistRow, index) => {
                        return (<Row gutter={[10, 5]} key={index}>
                            {artistRow.map((artist, index) => {
                                return (<Col span={4} key={index}>
                                    <Link to={`/artist/${artist.artistId}`}>
                                            <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                                    <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                            </Card>
                                    </Link>
                                </Col>)
                            })}
                        </Row>);
                    })}
            </>
        )
    }
}
