import React, { Component } from 'react'
import axios from 'axios';
import { Card, Layout, Typography, Menu, Table, Button } from 'antd';
import PubSub from 'pubsub-js';

const { Paragraph } = Typography;
const {Content} = Layout

const colomns = [
    {
        key: 'index',
        render: (text,record,index) => {return index + 1;}
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                axios({
                    method: 'GET',
                    url: 'http://localhost:3001/kugou',
                    params: {
                        r: 'play/getdata',
                        hash: record.musicId,
                        album_id: record.albumId,
                        mid: 1
                    }
                }).then((response) => {
                    record.playUrl = response.data.data.play_url;
                    PubSub.publish('play', record);  // record是包含url的MusicVO
                })
                // console.log(text);
                // console.log(record);
                // console.log(index);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '200px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
    }
];

export default class Artist extends Component {
    state = {data: []};

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist',
            params: {
                artistId: this.props.match.params.artistId
            }
        }).then((response) => {
            this.setState({data: response.data});
        });
    }

    getData = (params) => {
        
    }

    render() {
        const {data} = this.state;
        return (
            <div>
                <div style={{display: 'flex', height: '200px'}}>
                    <img alt={data.artistName} src={data.artistImg} />
                    <Card title={data.artistName} >
                        <div style={{overflow: 'auto'}}>
                            <Paragraph ellipsis={{rows: 3, expandable: true, suffix: '...', symbol: '展开'}}>
                                {data.artistDescription}
                            </Paragraph>
                        </div>
                        
                    </Card>
                </div>
                <Content style={{ padding: '10px', minHeight: 280}}>
                    <Menu mode="horizontal" defaultSelectedKeys="music" onClick={this.getData}>
                        <Menu.Item key="music">
                            单曲
                        </Menu.Item>
                        <Menu.Item key="album">
                            专辑
                        </Menu.Item>
                    </Menu>
                    <Table columns={colomns} dataSource={this.state.data.musicVOList} showHeader={false} pagination={{defaultPageSize: 30}} rowKey="musicId" />
                </Content>
                
            </div>
        )
    }
}
