import {makeObservable, observable, action} from 'mobx';

class PlayBarStore {
    playList = [];

    index = 0;

    constructor() {
        makeObservable(this, {
            playList: observable,
            index: observable,
            setPlayList: action,
            addMusic: action,
            setIndex: action
        })
    }

    setPlayList = (newPlayList) => {
        this.playList = newPlayList;
    }

    addMusic = (music) => {
        this.playList.push(music);
    }

    setIndex = (newIndex) => {
        this.index = newIndex;
    }
}

export const playBarStore = new PlayBarStore();
