export function isLogin() {
    if (localStorage.getItem('token') || sessionStorage.getItem('token')) {
        return true;
    } else {
        return false;
    }
}

export function getToken() {
    var token = localStorage.getItem('token');
    return token ? token : sessionStorage.getItem('token');
}
