import React, { Component } from 'react'
import {StepBackwardOutlined, StepForwardOutlined, CaretRightOutlined, PauseOutlined, UnorderedListOutlined} from '@ant-design/icons';
import { Button, Dropdown, Menu } from 'antd'
import PubSub from 'pubsub-js';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class PlayBar extends Component {
    state = {
        // 播放列表
        playList: [],
        // 当前播放序号
        index: 0,
        paused: true,
        currentTime: 0,
        duration: 0,
        volume: 1,
        beginTime: new Date().getTime()
    }

    componentDidMount() {
        this.token = PubSub.subscribe('play', this.playMusic);
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.token);
    }

    // 收到通知，播放音乐，data为music对象
    playMusic = (msg, data) => {
        this.addPlayTimes();
        const playList = this.state.playList;
        for (let i = 0; i < playList.length; i++) {
            if (playList[i].musicId === data.musicId) {
                this.setState({index: i});
                return;
            }
        }
        const newPlayList = [...playList, data];
        this.setState({playList: newPlayList, index: newPlayList.length - 1});
    }

    playBackward = () => {
        this.addPlayTimes();
        const newIndex = (this.state.index - 1 + this.state.playList.length) % this.state.playList.length;
        this.setState({index: newIndex});
    }
    
    playOrPause = () => {
        this.state.paused ? this.audio1.play() : this.audio1.pause();
        this.setState((state) => {
            return {paused: !state.paused};
        });
    }

    playForward = () => {
        this.addPlayTimes();
        const newIndex = (this.state.index + 1) % this.state.playList.length;
        this.setState({index: newIndex});
    }

    // 播放列表切换歌
    changeMusic = (obj) => {
        // console.log(obj.key);
        this.addPlayTimes();
        const index = this.state.playList.findIndex((music) => {
            return music.musicId === obj.key;
        });
        this.setState({index});
    }

    // 播放列表
    createMenu = () => {
        if (this.state.playList.length > 0) {
            return (
                <Menu selectedKeys={[this.state.playList[this.state.index].musicId]} onClick={this.changeMusic} style={{width: '800px', height: '300px', overflow: 'scroll', overflowX: 'hidden'}}>
                    <Menu.Item key="title" style={{width: '750px'}}>播放列表 ({this.state.playList.length})</Menu.Item>
                    <Menu.Divider />
                    {this.state.playList.map((music) => {
                        return (
                            <>
                                <Menu.Item key={music.musicId}>
                                    <div style={{display: 'flex'}}>
                                        <div style={{width: '450px'}}>{music.musicName}</div>
                                        <div style={{width: '250px'}}>{music.artistName}</div>
                                        <div style={{width: '50px'}}>{this.parseTime(music.timeLength / 1000)}</div>
                                    </div>
                                </Menu.Item>
                                <Menu.Divider />
                            </>
                        );
                    })}
                </Menu>
            );
        } else {
            return (
                <Menu>
                    <Menu.Item key="title" style={{width: '650px'}}>播放列表 ({this.state.playList.length})</Menu.Item>
                    <Menu.Divider />
                </Menu>
            );
        }
        
    }

    // 音乐加载完毕，可以播放
    canPlay = () => {
        this.setState({duration: this.audio1.duration, paused: false, volume: this.audio1.volume, beginTime: new Date().getTime()});
        this.audio1.play();
    }

    // audio更新state时间
    updatePlayTime = () => {
        this.setState({currentTime: this.audio1.currentTime});
    }

    // 停留时间超过1分钟，增加播放量
    addPlayTimes = () => {
        if (this.state.playList.length > 0) {
            const endTime = new Date().getTime();
            if (endTime - this.state.beginTime >= 60000) {
                axios({
                    method: 'POST',
                    url: 'http://localhost:3001/server/music/play_times',
                    data: {
                        musicId: this.state.playList[this.state.index].musicId
                    }
                }).then((response) => {
                    console.log(response.data);
                });
            }
        }
    }
    
    // 拖动条更新时间
    changePlayTime = (event) => {
        this.audio1.currentTime = event.target.value;
    }
    
    // 解析时间成00:00格式, time以秒为单位
    parseTime = (time) => {
        var min = Math.floor(time / 60);
        if (min < 10) {
            min = '0' + min;
        }
        var sec = Math.floor(time % 60);
        if (sec < 10) {
            sec = '0' + sec;
        }
        return min + ':' + sec;
    }
    
    // audio更新音量
    updateVolume = () => {
        this.setState({volume: this.audio1.volume});
    }

    // 静音或者音量最大
    mute = () => {
        if (this.state.playList.length > 0) {
            this.audio1.volume = this.audio1.volume > 0 ? 0 : 1;
        }
    }

    changeVolume = (event) => {
        // this.setState({volume: parseInt(event.target.value)});
        // if (this.state.playList.length > 0) {
        //     this.audio1.volume = event.target.value;
        // }
        this.audio1.volume = event.target.value;
    }

    render() {
        const {currentTime, duration, volume, playList, index} = this.state;
        return (
            <>
                <div style={{position: 'fixed', left: 0, bottom: 0, height: '60px', backgroundColor: 'black', width: '100%'}}>
                    <div style={{margin: '5px auto', width: '980px', display: 'flex'}} id="playBar">
                        <Button shape='circle' style={{backgroundColor: 'black', margin: 'auto 5px', borderWidth: '2px'}} onClick={this.playBackward}>
                            <StepBackwardOutlined style={{color: 'white', fontSize: '25px', marginTop: '-2px'}} />
                        </Button>
                        <Button shape='circle' style={{backgroundColor: 'black', margin: 'auto 5px', borderWidth: '2px'}} onClick={this.playOrPause} size="large">
                            {this.state.paused ? <CaretRightOutlined style={{color: 'white', fontSize: '32px', marginLeft: '4px', marginTop: '-4px'}} /> : <PauseOutlined style={{color: 'white', fontSize: '32px', marginLeft: '1px', marginTop: '-4.5px'}} />}
                        </Button>
                        <Button shape='circle' style={{backgroundColor: 'black', margin: 'auto 5px', borderWidth: '2px'}} onClick={this.playForward}>
                            <StepForwardOutlined style={{color: 'white', fontSize: '25px', marginTop: '-2px'}} />
                        </Button>
                        {playList.length > 0 ? 
                        <Link to={`/album/${playList[index].albumId}`}>
                            <img alt={playList[index].musicName} src={playList[index].albumImg} style={{width: '50px', height: '50px', margin: '0 10px'}} />
                        </Link> 
                        : <img alt="718音乐" src="/favicon.ico" style={{width: '50px', height: '50px', margin: '0 10px'}} />}
                        <div style={{width: '550px'}}>
                            <div style={{color: 'white'}}>
                                {playList.length > 0 ? playList[index].musicName : '718音乐'}
                                {playList.length > 0 && <span style={{fontSize: '10px', margin}}>{playList[index].artistName}</span>}
                            </div>
                            <div className="barAndTime" style={{marginTop: '8px', width: '550px', display: 'flex'}}>
                                <div className="bar" style={{width: '450px', height: '10px', marginTop: '7px', display: 'flex'}}>
                                    <input type="range" max={duration} value={currentTime} style={{backgroundColor: 'red', height: '10px', width: '450px', marginBottom: '5px'}} onChange={this.changePlayTime} />
                                    {/* <Button shape="circle" icon={<FireOutlined />} type="primary" size="small" style={{marginLeft: '-12px', marginTop: '-6px'}} /> */}
                                </div>
                                <div className="time" style={{color: 'white', marginLeft: '5px'}}>{this.parseTime(currentTime)}/{this.parseTime(duration)}</div>
                            </div>
                        </div>
                        <Dropdown overlay={this.createMenu} placement="topCenter" arrow getPopupContainer={() => document.getElementById('playBar')}>
                            <Button style={{marginTop: '10px', border: '0'}} icon={<UnorderedListOutlined />} ghost></Button>
                        </Dropdown>
                        <div className="volumeCtrl" style={{marginLeft: '10px', display: 'flex'}}>
                            <Button icon={volume !== 0 ? <i className="fa fa-volume-up"></i> : <i className="fa fa-volume-off"></i>} style={{marginTop: '10px', border: '0'}} ghost onClick={this.mute} />
                            <input type="range" step="0.01" max="1" value={volume} onChange={this.changeVolume} />
                        </div>
                    </div>
                </div>
                <audio src={playList.length > 0 ? playList[index].playUrl : ''} proload="auto" onCanPlay={this.canPlay} onTimeUpdate={this.updatePlayTime} onVolumeChange={this.updateVolume} onEnded={this.playForward} ref={r => this.audio1 = r}></audio>
            </>
        )
    }
}
