import axios from "axios";

export function getMusicUrl(record) {
    axios({
        method: 'GET',
        url: 'http://localhost:3001/kugou',
        params: {
            r: 'play/getdata',
            hash: record.musicId,
            album_id: record.albumId,
            mid: '39f0247247ae8d32fa08e059894d0361',
            appid: '1014',
            _: new Date().getTime()
        },
        withCredentials: true
    }).then((response) => {
        const playUrl = response.data.data.play_url;
    });
    return playUrl;  // record是包含url的MusicVO
}
