import React, { Component } from 'react'
import axios from 'axios';
import {Row, Col, Card} from 'antd'

const {Meta} = Card;

export default class ArtistList extends Component {
    state = {data: []};

    componentDidMount() {
        console.log(this.props.match.params);
        axios({
            method: 'GET',
            url: 'http://localhost:3001/artist/cat',
            params: {
                artistType: this.props.match.params.artistType
            }
        }).then((response) => {
            console.log(response.data);
            let i = 0;
            var newData = [];
            while (i < response.data.length) {
                newData.push(response.data.slice(i, Math.min(i + 4, response.data.length)));
                i += 4;
            }
            this.setState({data: newData});
        });
    }

    render() {
        let index = 0;
        return (
            <>
                {this.state.data.map((artistRow) => {
                   return (<Row gutter={16}>
                       {artistRow.map()}
                   </Row>);
                })}
            </>
        )
    }
}
