import { Layout, Menu } from 'antd'
import React, { Component } from 'react';
import {Link, Route} from 'react-router-dom'
import ArtistList from './ArtistList';

const { Content, Sider } = Layout;

export default class ArtistCat extends Component {
    state = {data: []};

    componentDidMount() {
        var linkObj = document.getElementById('hot');
        linkObj.click();
    }

    render() {
        return (
            <Layout>
                <Sider style={{width: 250}}>
                    <Menu mode="inline" defaultSelectedKeys="hot" style={{height: '100%'}}>
                        <Menu.Item key="hot">
                            <Link to="/artist_list/hot" id="hot">热门歌手</Link>
                        </Menu.Item>
                        <Menu.Item key="male">
                            <Link to="/artist_list/0">男歌手</Link>
                        </Menu.Item>
                        <Menu.Item key="female">
                            <Link to="/artist_list/1">女歌手</Link>
                        </Menu.Item>
                        <Menu.Item key="group">
                            <Link to="/artist_list/2">组合/乐队</Link>
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Content style={{ padding: '0 15px', minHeight: 280}}>
                    {this.state.data.map((artistRow) => {
                   return (<Row gutter={10}>
                       {artistRow.map((artist) => {
                           return (<Col span={4}>
                               <Link to={`/artist/${artist.artistId}`}>
                                    <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                            <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                    </Card>
                               </Link>
                           </Col>)
                       })}
                   </Row>);
                })}
                </Content>
            </Layout>
        )
    }
}
