import axios from 'axios';
import React, { Component } from 'react'
import {Menu, Table, Button, Row, Col, Card} from 'antd';
import { Link } from 'react-router-dom';
import { playMusic } from '../../utils/play';

const {Meta} = Card;
const musicColomns = [
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        title: '时长',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
];

const albumColomns = [
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (
            <Link to={`/album/${record.albumId}`} style={{display: 'flex'}}>
                <img alt={text} style={{width: '80px', height: '80px'}} src={record.albumImg} />
                <div style={{margin: 'auto 10px'}}>{text}</div>
            </Link>
            );
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumPublishTime',
        key: 'albumPublishTime',
        title: '发行时间',
        width: '200px'
    }
];

export default class SearchResult extends Component {
    state = {data: [], type: '', isUpdated: false, word: ''}

    componentDidMount() {
        this.setState({isUpdated: false});
        const {search} = this.props.location;
        const searchParams = new URLSearchParams(search.substring(1));
        const word = searchParams.get('word');
        const type = searchParams.get('type');
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/search',
            params: {
                word: word,
                type: type
            }
        }).then((response) => {
            console.log(response);
            if (response.data.status === 1) {
                this.setState({data: response.data.data, type: type, word: word}, () => {
                    this.setState({isUpdated: true});
                });
            }
        });
    }

    handleClick = (obj) => {
        this.props.history.push(`/search?word=${this.state.word}&type=${obj.key}`);
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
        this.setState({isUpdated: false});
        const {search} = nextProps.location;
        const searchParams = new URLSearchParams(search.substring(1));
        const word = searchParams.get('word');
        const type = searchParams.get('type');
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/search',
            params: {
                word: word,
                type: type
            }
        }).then((response) => {
            if (response.data.status === 1) {
                this.setState({type: type, word: word});
                if (type === 'artist') {
                    let i = 0;
                    let newData = [];
                    while (i < response.data.data.length) {
                        newData.push(response.data.data.slice(i, Math.min(i + 6, response.data.data.length)));  //拆成二维数组，为了下面排版
                        i += 6;
                    }
                    this.setState({data: newData}, () => {
                            this.setState({isUpdated: true});
                    });
                } else {
                    this.setState({data: response.data.data}, () => {
                            this.setState({isUpdated: true});
                    });
                }
            }
        });
    }

    render() {
        const {data, type, isUpdated} = this.state;
        return (
            <>
                <Menu onClick={this.handleClick} mode="horizontal" selectedKeys={type}>
                    <Menu.Item key="music">
                        单曲
                    </Menu.Item>
                    <Menu.Item key="artist">
                        歌手
                    </Menu.Item>
                    <Menu.Item key="album">
                        专辑
                    </Menu.Item>
                </Menu>
                {isUpdated && type === 'music' && <Table dataSource={data} columns={musicColomns} pagination={{defaultPageSize: 20}} />}
                {/* {isUpdated && type === 'artist' && (data.length > 0 ? data.map((artistRow, index) => {
                        return (<Row gutter={[10, 5]} key={index}>
                            {artistRow.map((artist, index) => {
                                return (<Col span={4} key={index}>
                                    <Link to={`/artist/${artist.artistId}`}>
                                            <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                                    <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                            </Card>
                                    </Link>
                                </Col>)
                            })}
                        </Row>);
                }) : <Table dataSource={[]} showHeader={false} />)} */}
                {isUpdated && type === 'artist' && data.map()}
                {isUpdated && type === 'album' && <Table dataSource={data} columns={albumColomns} pagination={{defaultPageSize: 20}} />}
            </>
        )
    }
}
