import React, { Suspense, useEffect, useState, useRef } from 'react'
import { Layout, Menu, Image, Skeleton, Button } from 'antd'
import axios from 'axios';
import PubSub from 'pubsub-js';
import {PlusCircleOutlined, DeleteOutlined} from '@ant-design/icons';
import {renderRoutes} from 'react-router-config';
import { getToken, removeToken } from '../../utils/auth';
import { SERVER_URL } from '../../utils/constant';

const { Content, Sider } = Layout;
const {SubMenu} = Menu;

export default function MyMusic(props) {
    const [collectionLists, setCollectionLists] = useState([]);
    const deleteButton = useRef();

    useEffect(() => {
        PubSub.subscribe('getCollectionList', getCollectionList);
        getCollectionList();  
        return () => {
            PubSub.unsubscribe('getCollectionList');
        }  // eslint-disable-next-line
    }, []);

    const handleClick = (obj) => {
        if (obj.key === 'newList') {
            PubSub.publish('showNewCollectionListModal');
            getCollectionList();
        } else if (obj.key === 'ranking') {
            props.history.push('/my_music/ranking');
        } else {
            props.history.push('/my_music/collection_list/' + obj.key);
        }
    }

    const getCollectionList = () => {
        axios({
            method: 'GET',
            url: SERVER_URL + 'collection_list',
            headers: {
                Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
                props.history.push('/');
            } else if (response.data.status === 1) {
                setCollectionLists(response.data.data);
            }
        });
    }

    const handleMouseEnter = () => {
        deleteButton.current.show = 'block';
    }

    return (
        <Layout style={{minHeight: '100vh'}}>
            <Sider>
                <Menu mode="inline" defaultSelectedKeys="ranking" style={{height: '100%'}} onClick={handleClick} defaultOpenKeys={['collection_list']}>
                    <Menu.Item key="ranking">听歌排行</Menu.Item>
                    <SubMenu key="collection_list" title="我的歌单">
                        <Menu.Item key="newList" icon={<PlusCircleOutlined />}>创建新歌单</Menu.Item>
                        <Menu.Divider />
                        {collectionLists.map((list) => {
                            return (
                            <div style={{width: '200px', height: '50px', display: 'flex'}}>
                                <Menu.Item key={list.collectionListId} style={{paddingLeft: '10px', height: '50px', width: '170px'}} onMouseEnter={handleMouseEnter}>
                                    {/* <div style={{height: '40px', display: 'flex'}}>
                                        <Image width="40px" src={list.collectionListImg} preview={false} />
                                        <div style={{marginLeft: '10px', width: '100px', height: '40px', overflow: 'hidden'}}>{list.collectionListName}</div>
                                        <div style={{color: '#999', margin: 'auto 0 auto auto'}}>{list.collectionListLength}首</div>
                                    </div> */}
                                    <div style={{width: '170px', height: '40px', display: 'flex'}}>
                                        <img src={list.collectionListImg} style={{width: '40px', height: '40px', marginRight: '10px'}} />
                                        <div style={{width: '100px', height: '40px'}}>
                                            <div style={{width: '100px', height: '20px', lineHeight: '15px', fontSize: '15px'}}>{list.collectionListName}</div>
                                            <div style={{width: '100px', height: '20px', lineHeight: '25px', fontSize: '13px'}}>{list.collectionListLength}首</div>
                                        </div>
                                    </div>
                                </Menu.Item>
                                <Button icon={<DeleteOutlined />} style={{width: '20px', height: '20px', marginTop: '25px'}} onClick={deleteCollectionList()} />
                            </div>);
                        })}
                    </SubMenu>
                    
                </Menu>
            </Sider>
            <Content style={{ padding: '0', minHeight: 280}}>
                <Suspense fallback={<Skeleton />}>{renderRoutes(props.route.routes)}</Suspense>
            </Content>
        </Layout>
    );
}
