import axios from 'axios';
import React, { Component } from 'react'
import {Menu, Table} from 'antd';

const colomns = [
    {
        key: 'index',
        render: (text,record,index) => {return index + 1;}
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        title: '时长',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
];

export default class SearchResult extends Component {
    static getDerivedStateFromProps(props, state) {
        const {search} = props.location;
        const searchParams = new URLSearchParams(search.substring(1));
        const word = searchParams.get('word');
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/search',
            params: {
                word: word
            }
        }).then((response) => {
            if (response.data.status === 1) {
                state.musicData = response.data.data;
                state.word = word;
            }
        });
    }

    handleClick = (obj) => {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/search',
            params: {
                word: this.state.word,
                type: obj.key
            }
        }).then((response) => {
            if (response.data.status === 1) {
                if (obj.key === 'music') {
                    this.setState({musicData: response.data.data});
                } else if (obj.key === 'artist') {
                    this.setState({artistData: response.data.data});
                } else {
                    this.setState({albumData: response.data.data});
                }
            }
        })
    }

    render() {
        const {musicData, artistData, albumData} = this.state;
        return (
            <>
                <Menu onClick={this.handleClick} mode="horizontal" defaultSelectedKeys="music">
                    <Menu.Item key="music">
                        单曲
                    </Menu.Item>
                    <Menu.Item key="artist">
                        歌手
                    </Menu.Item>
                    <Menu.Item key="album">
                        专辑
                    </Menu.Item>
                </Menu>
                <Table dataSource={musicData} columns={colomns} pagination={{defaultPageSize: 20}} />
            </>
        )
    }
}
