import axios from "axios";

export function getPlayUrl(record) {
    console.log(record);
    let playUrl;
    axios({
        method: 'GET',
        url: 'http://localhost:3001/kugou',
        params: {
            r: 'play/getdata',
            hash: record.musicId,
            album_id: record.albumId,
            mid: '39f0247247ae8d32fa08e059894d0361',
            appid: '1014',
            _: new Date().getTime()
        },
        withCredentials: true
    }).then((response) => {
        playUrl = response.data.data.play_url;
    });
    console.log(playUrl);
    return playUrl;
}
