import React, {useEffect, useState} from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom'

import {Button, Table} from 'antd';
import { getToken, removeToken } from '../../../utils/auth';
import { SERVER_URL } from '../../../utils/constant';
import { playMusic } from '../../../utils/play';

const colomns = [
    {
        key: 'index',
        render: (text, record, index) => {
            return index + 1;
        }
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        ellipsis: true,
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'userPlayTimes',
        key: 'userPlayTimes',
        title: '播放次数',
        render: (text) => {
            return text + '次';
        },
        width: '100px'
    }
];

export default function Ranking(props) {
    const [ranking, setRanking] = useState([]);
    
    useEffect(() => {
        axios({
            method: 'GET',
            url: SERVER_URL + 'user/music_ranking',
            headers: {
              Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
                props.history.push('/');
            } else if (response.data.status === 1) {
                setRanking(response.data.data);
            }
        });  // eslint-disable-next-line
    }, []);

    return (
        <>
            <Table dataSource={ranking} columns={colomns} title={() => '听歌排行'} rowKey={record => record.musicId} />
        </>
    );
}
