import axios from 'axios'
import React, { Component } from 'react';
import PubSub from 'pubsub-js';
import { Image, Button, Table, Layout } from 'antd';
import { Link } from 'react-router-dom';

const {Content} = Layout;
const colomns = [
    {
        key: 'index',
        render: (text,record,index) => {return index + 1;}
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                axios({
                    method: 'GET',
                    url: 'http://localhost:3001/kugou',
                    params: {
                        r: 'play/getdata',
                        hash: record.musicId,
                        album_id: record.albumId,
                        mid: 1
                    }
                }).then((response) => {
                    record.playUrl = response.data.data.play_url;
                    PubSub.publish('play', record);  // record是包含url的MusicVO
                })
                // console.log(text);
                // console.log(record);
                // console.log(index);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        render: (text, record) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'timeLength',
        key: 'timeLength',
        render: (text) => {
            text = parseInt(text);
            text /= 1000;
            var min = Math.floor(text / 60);
            if (min < 10) {
                min = '0' + min;
            }
            var sec = Math.floor(text % 60);
            if (sec < 10) {
                sec = '0' + sec;
            }
            return (<span>{min + ':' + sec}</span>);
        },
        width: '100px'
    }
];

export default class Album extends Component {
    state = {data: {}, updated: false};

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/album',
            params: {
                albumId: this.props.match.params.albumId
            }
        }).then((response) => {
            console.log(response.data);
            this.setState({data: response.data}, () => {
                this.setState({updated: true});
            });
        });
    }

    render() {
        const {data, updated} = this.state;
        return (
            updated && <>
                <div className="info1" style={{display: 'flex'}}>
                    <Image src={data.albumImg} />
                    <div className="info2" style={{marginLeft: '50px'}}>
                        <h2>{data.albumName}</h2>
                        <p>歌手：{data.artistName}</p>
                        <p>发行日期：{data.albumPublishTime}</p>
                    </div>
                </div>
                <Content style={{padding: '10px'}}>
                    <div style={{display: 'flex', margin: '20px 0'}}>
                        <h2>包含歌曲列表</h2>
                        <span style={{marginBottom: '0'}}>{data.musicVOList.length}首歌</span>
                    </div>
                    <Table columns={colomns} dataSource={data.musicVOList} showHeader={false} pagination={{defaultPageSize: 20}} rowKey="musicId" />
                </Content>
            </>
        )
    }
}
