import React, { Component } from 'react'
import axios from 'axios';
import {Row, Col, Card} from 'antd'

const {Meta} = Card;

export default class ArtistList extends Component {
    state = {data: []};

    componentDidMount() {
        console.log(this.props.match.params);
        axios({
            method: 'GET',
            url: 'http://localhost:3001/artist/cat',
            params: {
                artistType: this.props.match.params.artistType
            }
        }).then((response) => {
            console.log(response.data);
            this.setState({data: response.data});
        });
    }

    generateList
    render() {
        let index = 0;
        return (
            <>
                {this.state.data.map((artist, index) => {
                   return (<Col gutter={6}>
                       <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                           <Meta title={artist.artistName} style={{textAlign: 'center'}}/>
                       </Card>
                   </Col>);
                })}
            </>
        )
    }
}
