import React, {Suspense, useState, useEffect } from 'react';
import { Layout, Skeleton } from 'antd';
import {BrowserRouter, HashRouter} from 'react-router-dom';
import PubSub from 'pubsub-js';
import { renderRoutes } from 'react-router-config';
import routes from './router';
import axios from 'axios';
import PlayBar from './components/PlayBar';
import MyHeader from './components/MyHeader';
import { getToken, removeToken } from './utils/auth';
import 'antd/dist/antd.min.css';
import './App.css';


const { Content, Footer } = Layout;

export default function App() {
    const [user, setUser] = useState(undefined);

    useEffect(() => {
        PubSub.subscribe('login', login);
        PubSub.subscribe('logout', logout);
        return () => {
            PubSub.unsubscribe('login');
            PubSub.unsubscribe('logout');
        }
    }, []);

    useEffect(() => {
        const token = getToken();
        if (token) {
            axios({
                method: 'POST',
                url: 'http://localhost:3001/server/verify',
                headers: {
                    Authorization: token
                }
            }).then((response) => {
                if (response.data.status === 1) {
                    localStorage.setItem('token', response.data.token);
                    setUser(response.data.data);
                } else {
                    removeToken();
                }
            });
        }
    }, []);

    const login = (message, user) => {
        setUser(user);
    };

    const logout = () => {
        removeToken();
        setUser(undefined);
    }

    return (
        <HashRouter>
            <Layout className="layout" style={{minHeight: '100vh'}}>
                <MyHeader user={user} />
                <Content style={{ width: '1000px', margin: '0 auto'}}>
                    <div className="site-layout-content">
                        <Suspense fallback={<Skeleton />}>
                            {renderRoutes(routes)}
                        </Suspense>
                    </div>
                </Content>
                <Footer style={{ textAlign: 'center', height: '150px' }}>718音乐 ©2021 Created by YWJ</Footer>
            </Layout>
            <PlayBar user={user} />
        </HashRouter>
    );
}
