import React, { Component } from 'react'
import { Button, Menu, Input, Modal, Form, Checkbox, Dropdown, Layout } from 'antd';
import {Link, withRouter} from 'react-router-dom';
import 'antd/dist/antd.min.css';
import { LockOutlined, UserOutlined, LogoutOutlined } from '@ant-design/icons';
import axios from 'axios';
import {getToken, removeToken} from '../../utils/auth.js';
import PubSub from 'pubsub-js';

const { Header } = Layout;
const {Search} = Input;

class MyHeader extends Component {
    state = {isLoginModalVisible: false, isRegisterModalVisible: false};

    componentDidMount() {
        this.sub1 = PubSub.subscribe('showLoginModal', this.showLoginModal);
        const token = getToken();
        if (token) {
            axios({
                method: 'POST',
                url: 'http://localhost:3001/server/verify',
                headers: {
                    Authorization: token
                }
            }).then((response) => {
                console.log(response);
                if (response.data.status === 1) {
                    localStorage.setItem('token', response.data.token);
                    this.setState({user: response.data.data});
                } else {
                    removeToken();
                }
            });
        }
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.sub1);
    }
    
    logout = (obj) => {
        if (obj.key === 'logout') {
            removeToken();
            this.setState({user: undefined});
        }
    }

    userMenu = () => {
        return (
            <Menu onClick={this.logout}>
                <Menu.Item key="home" icon={<UserOutlined />}>
                    我的主页
                </Menu.Item>
                <Menu.Item key="logout" icon={<LogoutOutlined />}>
                    退出
                </Menu.Item>
            </Menu>
        );
    }

    showLoginModal = () => {
        this.closeRegisterModal();
        this.setState({isLoginModalVisible: true});
    }

    closeLoginModal = () => {
        this.setState({isLoginModalVisible: false});
    }

    showRegisterModal = () => {
        this.closeLoginModal();
        this.setState({isRegisterModalVisible: true});
    }

    closeRegisterModal = () => {
        this.setState({isRegisterModalVisible: false});
    }

    login = (value) => {
        axios({
            method: 'POST',
            url: 'http://localhost:3001/server/login',
            data: {
                username: value.username,
                password: value.password
            }
        }).then((response) => {
            if (response.data.status !== 1) {
                this.alert1.innerHTML = response.data.message;
                this.alert1.style.display = 'block';
            } else {
                if (value.remember) {
                    localStorage.setItem('token', response.data.token);
                } 
                sessionStorage.setItem('token', response.data.token);
                this.setState({user: response.data.data});
                this.closeLoginModal();
            }
        })
    }

    register = (value) => {
        if (value.confirm !== value.password) {
            this.alert2.innerHTML = '密码不一致';
            this.alert2.style.display = 'block';
            return;
        }
        axios({
            method: 'POST',
            url: 'http://localhost:3001/server/register',
            data: {
                username: value.username,
                password: value.password,
                nickname: value.nickname
            }
        }).then((response) => {
            if (response.data.status === 1) {
                sessionStorage.setItem('token', response.data.token);
                this.setState({user: response.data.data});
                this.closeRegisterModal();
            } else {
                this.alert2.innerHTML = response.data.message;
                this.alert2.style.display = 'block';
            }
        });
    }

    search = (value) => {
        if (value.trim() !== '') {
            this.props.history.push('/search?word=' + value + '&type=music');
        }
    }

    handleClick = (obj) => {
        if (obj.key === 'my') {
            if (this.state.user) {
                this.props.history.push('/my_music/rank');
            } else {
                this.showLoginModal();
            }
        } else if (obj.key === 'artist') {
            this.props.history.push('/artist_list?artistLocation=hot&artistType=hot');
        }
    }

    render() {
        return (
            <Header>
                <div style={{width: '1100px', margin: '0 auto', display: 'flex'}}>
                    <div className="logo" style={{color: 'white'}}>
                        <span className="fa fa-music"></span>&nbsp;
                        718音乐
                    </div>
                    <Menu theme="dark" mode="horizontal" defaultSelectedKeys="recommend" style={{marginLeft: 100}} onClick={this.handleClick}>
                        <Menu.Item key="recommend">推荐</Menu.Item>
                        <Menu.Item key="ranking">排行榜</Menu.Item>
                        <Menu.Item key="artist">歌手</Menu.Item>
                        <Menu.Item key="my">我的音乐
                        </Menu.Item>
                    </Menu>
                    <Search placeholder="搜索" style={{ width: '200px', display: 'block', margin: 'auto 50px auto 50px'}} onSearch={this.search} />
                    {this.state.user ? <Dropdown overlay={this.userMenu}><Button type="link" style={{color: 'yellow', display: 'block', margin: 'auto 0 auto auto'}}>{this.state.user.username}</Button></Dropdown>
                    : <Button type="primary" style={{display: 'block', margin: 'auto 0 auto auto'}} onClick={this.showLoginModal}>登录</Button>}
                    <Modal title="登录" visible={this.state.isLoginModalVisible} onCancel={this.closeLoginModal} footer={[
                        <Button key="register" type="primary" style={{float: 'left'}} onClick={this.showRegisterModal}>注册</Button>,
                        <Button key="cancel" onClick={this.closeLoginModal}>取消</Button>,
                    ]}>
                        <Form onFinish={this.login}>
                            <div ref={r => this.alert1 = r} style={{backgroundColor: '#ffccc7', width: '100%', padding: '8px 15px', marginBottom: '10px', display: 'none'}}></div>
                            <Form.Item name="username" rules={[{ required: true, message: '用户名不能为空!' }]}>
                                <Input prefix={<UserOutlined />} placeholder="请输入用户名" />
                            </Form.Item>
                            <Form.Item name="password" rules={[{required: true, message: '密码不能为空!'}]}>
                                <Input.Password prefix={<LockOutlined />} placeholder="请输入密码" />
                            </Form.Item>
                            <Form.Item>
                                <Form.Item name="remember" valuePropName="checked" noStyle>
                                    <Checkbox>记住我</Checkbox>
                                </Form.Item>
                                {/* <a className="login-form-forgot" href="" style={{float: 'right'}}>忘记密码</a> */}
                            </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit" style={{width: '100%'}}>登录</Button>
                            </Form.Item>
                        </Form>
                    </Modal>
                    <Modal title="注册" visible={this.state.isRegisterModalVisible} destroyOnClose onCancel={this.closeRegisterModal} footer={[
                        <Button key="register" type="primary" style={{float: 'left'}} onClick={this.showLoginModal}>登录</Button>,
                        <Button key="cancel" onClick={this.closeRegisterModal}>取消</Button>,
                    ]}>
                        <Form onFinish={this.register}>
                            <div ref={r => this.alert2 = r} style={{backgroundColor: '#ffccc7', width: '100%', padding: '8px 15px', marginBottom: '10px', display: 'none'}}></div>
                            <Form.Item name="username" label="用户名" rules={[{ required: true, message: '用户名不能为空!'}]}>
                                <Input placeholder="请输入用户名" />
                            </Form.Item>
                            <Form.Item name="password" label="密码" rules={[{required: true, message: '密码不能为空!'}]}>
                                <Input.Password placeholder="请输入密码" />
                            </Form.Item>
                            <Form.Item name="confirm" label="确认密码" dependencies={['password']} rules={[
                                {required: true, message: '密码不能为空!'},
                                ({ getFieldValue }) => ({
                                    validator(_, value) {
                                        if (!value || getFieldValue('password') === value) {
                                            return Promise.resolve();
                                        }
                                        return Promise.reject(new Error('密码不一致!'));
                                    },
                                })
                            ]}>
                                <Input.Password placeholder="请再次输入密码" />
                            </Form.Item>
                            <Form.Item name="nickname" label="昵称" rules={[{required: true, message: '昵称不能为空!'}]}>
                                <Input placeholder="请输入昵称" />
                            </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit" style={{width: '100%'}}>注册</Button>
                            </Form.Item>
                        </Form>
                    </Modal>
                </div>
            </Header>
        )
    }
}

export default withRouter(MyHeader);
