import React, { Component } from 'react'
import axios from 'axios';
import { Card, Descriptions, Typography } from 'antd';
import './index.css';

const {Meta} = Card;
const { Paragraph } = Typography;

export default class Artist extends Component {
    state = {data: []};

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/artist',
            params: {
                artistId: this.props.match.params.artistId
            }
        }).then((response) => {
            this.setState({data: response.data});
        });
    }

    render() {
        const {data} = this.state;
        return (
            <div>
                <div className="artistCard">
                    <img alt={data.artistName} src={data.artistImg} />
                    <Card title={data.artistName} style={{overflow: sc}}>
                        <Paragraph ellipsis={{rows: 4, expandable: true, suffix: '...', symbol: '展开'}}>
                            {data.artistDescription}
                        </Paragraph>
                    </Card>
                </div>
                
            </div>
        )
    }
}
