import React, { Component } from 'react'
import axios from 'axios';
import {Row, Col, Card} from 'antd'
import {Link} from 'react-router-dom'

const {Meta} = Card;

export default class ArtistList extends Component {
    state = {data: []};

    componentDidUpdate() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist_list',
            params: {
                artistType: this.props.match.params.artistType
            }
        }).then((response) => {
            let i = 0;
            var newData = [];
            while (i < response.data.length) {
                newData.push(response.data.slice(i, Math.min(i + 6, response.data.length)));
                i += 6;
            }
            this.setState({data: newData});
        });
    }

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/artist/cat',
            params: {
                artistType: this.props.match.params.artistType
            }
        }).then((response) => {
            let i = 0;
            var newData = [];
            while (i < response.data.length) {
                newData.push(response.data.slice(i, Math.min(i + 6, response.data.length)));
                i += 6;
            }
            this.setState({data: newData});
        });
    }

    render() {
        return (
            <>
                {this.state.data.map((artistRow) => {
                   return (<Row gutter={10}>
                       {artistRow.map((artist) => {
                           return (<Col span={4}>
                               <Link to={`/artist/${artist.artistId}`}>
                                    <Card hoverable cover={<img alt={artist.artistName} src={artist.artistImg} />}>
                                            <Meta title={artist.artistName} style={{textAlign: 'center'}} />
                                    </Card>
                               </Link>
                           </Col>)
                       })}
                   </Row>);
                })}
            </>
        )
    }
}
