import React, { Component } from 'react'
import { Button, Layout, Menu, Input, Modal, Form, Checkbox, Dropdown } from 'antd';
import {Link, Route, Switch} from 'react-router-dom';
import 'antd/dist/antd.min.css';
import './App.css';
import { LockOutlined, UserOutlined, PoweroffOutlined } from '@ant-design/icons';
import axios from 'axios';
import {getToken, isLogin} from './utils/auth.js';

export default class MyHeader extends Component {
    render() {
        return (
            <Header>
                <div style={{width: '1100px', margin: '0 auto', display: 'flex'}}>
                    <div className="logo" style={{color: 'white'}}>
                        <span className="fa fa-music"></span>&nbsp;
                        718音乐
                    </div>
                    <Menu theme="dark" mode="horizontal" defaultSelectedKeys="recommend" style={{marginLeft: 100}}>
                        <Menu.Item key="recommend">推荐</Menu.Item>
                        <Menu.Item key="ranking">排行榜</Menu.Item>
                        <Menu.Item key="artist">
                            <Link to="/artist_list">歌手</Link>
                        </Menu.Item>
                        <Menu.Item key="my">我的音乐</Menu.Item>
                    </Menu>
                    <Search placeholder="input search text" style={{ width: '200px', display: 'block', margin: 'auto 50px auto 50px'}} />
                    {!isLogin() ? <Button type="primary" style={{display: 'block', margin: 'auto 0 auto auto'}} onClick={this.showLoginModal}>登录</Button>
                        : <Dropdown overlay={this.userMenu}><Button type="link" style={{color: 'yellow', display: 'block', margin: 'auto 0 auto auto'}}>{this.state.user.username}</Button></Dropdown>}
                    <Modal title="登录" visible={this.state.isLoginModalVisible} onCancel={this.closeLoginModal} footer={[
                        <Button key="register" type="primary" style={{float: 'left'}} onClick={this.showRegisterModal}>注册</Button>,
                        <Button key="cancel" onClick={this.closeLoginModal}>取消</Button>,
                    ]}>
                        <Form onFinish={this.login}>
                            <div ref={r => this.alert1 = r} style={{backgroundColor: '#ffccc7', width: '100%', padding: '8px 15px', marginBottom: '10px', display: 'none'}}></div>
                            <Form.Item name="username" rules={[{ required: true, message: '手机号不能为空!' }]}>
                                <Input prefix={<UserOutlined />} placeholder="请输入手机号" />
                            </Form.Item>
                            <Form.Item name="password" rules={[{required: true, message: '密码不能为空!'}]}>
                                <Input.Password prefix={<LockOutlined />} placeholder="请输入密码" />
                            </Form.Item>
                            <Form.Item>
                                <Form.Item name="remember" valuePropName="checked" noStyle>
                                    <Checkbox>记住我</Checkbox>
                                </Form.Item>
                                {/* <a className="login-form-forgot" href="" style={{float: 'right'}}>忘记密码</a> */}
                            </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit" style={{width: '100%'}}>登录</Button>
                            </Form.Item>
                        </Form>
                    </Modal>
                    <Modal title="注册" visible={this.state.isRegisterModalVisible} destroyOnClose onCancel={this.closeRegisterModal} footer={[
                        <Button key="register" type="primary" style={{float: 'left'}} onClick={this.showLoginModal}>登录</Button>,
                        <Button key="cancel" onClick={this.closeRegisterModal}>取消</Button>,
                    ]}>
                        <Form onFinish={this.register}>
                            <div ref={r => this.alert2 = r} style={{backgroundColor: '#ffccc7', width: '100%', padding: '8px 15px', marginBottom: '10px', display: 'none'}}></div>
                            <Form.Item name="username" label="手机号" rules={[{ required: true, message: '手机号不能为空!'}]}>
                                <Input placeholder="请输入手机号" />
                            </Form.Item>
                            <Form.Item name="password" label="密码" rules={[{required: true, message: '密码不能为空!'}]}>
                                <Input.Password placeholder="请输入密码" />
                            </Form.Item>
                            <Form.Item name="confirm" label="确认密码" dependencies={['password']} rules={[
                                {required: true, message: '密码不能为空!'},
                                ({ getFieldValue }) => ({
                                    validator(_, value) {
                                        if (!value || getFieldValue('password') === value) {
                                            return Promise.resolve();
                                        }
                                        return Promise.reject(new Error('密码不一致!'));
                                    },
                                })
                            ]}>
                                <Input.Password placeholder="请再次输入密码" />
                            </Form.Item>
                            <Form.Item name="nickname" label="昵称" rules={[{required: true, message: '昵称不能为空!'}]}>
                                <Input placeholder="请输入昵称" />
                            </Form.Item>
                            <Form.Item>
                                <Button type="primary" htmlType="submit" style={{width: '100%'}}>注册</Button>
                            </Form.Item>
                        </Form>
                    </Modal>
                </div>
            </Header>
        )
    }
}
