import React, {useEffect, useRef, useState} from 'react';
import {Button, Carousel, Col, Image, Row} from "antd";
import {LeftOutlined, RightOutlined} from "@ant-design/icons";
import axios from "axios";
import {SERVER_URL} from "../../utils/constant";
import {Link} from "react-router-dom";
import {playBarStore} from "../../stores/playBarStore";

export default function Discover() {
    const [musicList, setMusicList] = useState([]);
    const [albumList, setAlbumList] = useState([]);

    useEffect(() => {
        axios({
            method: 'GET',
            url: SERVER_URL + 'latestMusics'
        }).then((response) => {
            setMusicList(response.data.data);
        });
        axios({
            method: 'GET',
            url: SERVER_URL + 'latestAlbums'
        }).then((response) => {
            setAlbumList(response.data.data);
        });
    }, []);

    return (
        <>
            <div>
                <h1 style={{textAlign: 'center', marginBottom: '20px'}}>新歌首发</h1>
                <Row gutter={[0, 5]}>
                        {musicList.map((music, index) => {
                            return (<Col>
                                <div style={{height: '70px', display: 'flex', width: '315px'}}>
                                    <Link to={`/album/${musicList[index].albumId}`}>
                                        <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                    </Link>
                                    <div style={{height: '70px'}}>
                                        <Button type="link" onClick={() => {playBarStore.addMusic(musicList[index]);}} style={{padding: '0px'}}>
                                            <div style={{height: '35px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                        </Button>
                                        <Link to={`/artist/${musicList[index].artistId}`}>
                                            <div style={{height: '35px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                        </Link>
                                    </div>
                                </div>
                            </Col>);
                        })}
                </Row>
            </div>
            <div>
                <h1 style={{textAlign: 'center', marginBottom: '20px'}}>新碟首发</h1>
                <Row gutter={[0, 5]}>
                    {albumList.map((music, index) => {
                        return (<Col>
                            <div style={{height: '70px', display: 'flex', width: '315px'}}>
                                <Link to={`/album/${musicList[index].albumId}`}>
                                    <img alt={musicList[index].albumName} src={musicList[index].albumImg} style={{width: '70px', height: '70px', marginRight: '10px'}} />
                                </Link>
                                <div style={{height: '70px'}}>
                                    <Button type="link" onClick={() => {playBarStore.addMusic(musicList[index]);}} style={{padding: '0px'}}>
                                        <div style={{height: '35px', lineHeight: '15px', fontSize: '15px'}}>{musicList[index].musicName}</div>
                                    </Button>
                                    <Link to={`/artist/${musicList[index].artistId}`}>
                                        <div style={{height: '35px', lineHeight: '21px', fontSize: '13px', color: '#999'}}>{musicList[index].artistName}</div>
                                    </Link>
                                </div>
                            </div>
                        </Col>);
                    })}
                </Row>
            </div>
        </>
    );
}
