import {makeAutoObservable} from 'mobx';

export class PlayBarStore {
    playList = [];

    index = 0;

    constructor() {
        makeAutoObservable(this);
    }

    setPlayList = (newPlayList) => {
        this.playList = newPlayList;
    }

    setIndex = (newIndex) => {
        this.index = newIndex;
    }
}
