import {makeObservable, observable, action, toJS} from 'mobx';
import {getPlayUrl} from "../utils/play";

class PlayBarStore {
    playList = [];

    index = 0;

    playUrl = '';

    constructor() {
        makeObservable(this, {
            playList: observable,
            index: observable,
            playUrl: observable,
            setPlayList: action,
            addMusic: action,
            setIndex: action
        })
    }

    setPlayList = (newPlayList) => {
        this.playList = newPlayList;
    }

    setIndex = (newIndex) => {
        this.index = newIndex;
        getPlayUrl(toJS(this.playList[this.index]));
    }

    addMusic = (newMusic) => {
        let newList = this.playList.filter(music => music.musicId !== newMusic.musicId);
        newList.push(newMusic);
        this.setPlayList(newList);
        this.setIndex(newList.length - 1);
        // console.log(this.playList);
    }

    setPlayUrl = (newPlayUrl) => {
        this.playUrl = newPlayUrl;
    }
}

export const playBarStore = new PlayBarStore();
