import {observable, action, decorate} from 'mobx';


export class PlayBarStore {
    playList = [];

    index = 0;

    setPlayList = (newPlayList) => {
        this.playList = newPlayList;
    }

    setIndex = (newIndex) => {
        this.index = newIndex;
    }
}

decorate(PlayBarStore, {
    playList: observable,
    index: observable,
    setPlayList: action,
    setIndex: action
})
