import React, { Component } from 'react'
import {PlayCircleOutlined, RightCircleOutlined, LeftCircleOutlined} from '@ant-design/icons'
import { Button } from 'antd'

export default class PlayBar extends Component {
    state = {
        playList: [],
        index: 0
    }

    render() {
        return (
            <div style={{position: 'fixed', left: 0, bottom: 0, height: '60px', backgroundColor: 'black', width: '100%'}}>
                <div style={{margin: '5px auto', width: '60%'}} >
                    <Button icon={<LeftCircleOutlined />} shape='circle' style={{margin: '10px 5px',width: '40px', height: '40px'}}/>
                    <Button icon={<PlayCircleOutlined />} shape='circle' />
                    <Button icon={<RightCircleOutlined />} shape='circle' />
                    <LeftCircleOutlined style={{color: 'white', fontSize: '40px', margin: 'auto 5px'}} />
                    <PlayCircleOutlined style={{color: 'white', fontSize: '50px', margin: 'auto 5px'}} />
                    <RightCircleOutlined style={{color: 'white', fontSize: '40px', margin: 'auto 5px'}} />
                </div>
            </div>
        )
    }
}
