import React, { Component } from 'react'
import { Layout, Menu, Image, Button, Table } from 'antd'
import {Link} from 'react-router-dom'
import axios from 'axios';
import { getToken, removeToken } from '../../utils/auth';
import { playMusic } from '../../utils/play';

const { Content, Sider } = Layout;
const {SubMenu} = Menu;
const rankColomns = [
    {
        key: 'index',
        render: (text, record, index) => {
            return index + 1;
        }
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'userPlayTimes',
        key: 'userPlayTimes',
        title: '播放次数',
        render: (text) => {
            return text + '次';
        },
        width: '100px'
    }
];
export default class MyMusic extends Component {
    state = {collectionList: [], rankList: [], type: ''}

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/user/music_rank',
            headers: {
                Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
            } else if (response.data.status === 1) {
                this.setState({type: 'rank', rankList: response.data.data});
            }
        });
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/collection_list',
            headers: {
                Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
            } else if (response.data.status === 1) {
                this.setState({collectionList: response.data.data});
            }
        })
    }

    render() {
        const {rankList, collectionList, type} = this.state;
        return (
            <Layout>
                <Sider style={{width: 300}}>
                    <Menu mode="inline" selectedKeys={type} style={{height: '100%'}}>
                        <Menu.Item key="rank">
                            <Link to="/my_music/rank">听歌排行</Link>
                        </Menu.Item>
                        <SubMenu key="collection_list" title="创建的歌单">
                            {this.state.collectionList.map((list) => {
                                return (<Menu.Item key={list.collectionListId} style={{paddingLeft: '10px'}}>
                                    <Link to={`/my_music/collection_list/${list.collectionListId}`}>
                                        <div style={{display: 'flex'}}>
                                            <Image width="35px" src={list.collectionListImg} preview={false} />
                                            <div>
                                                <div style={{height: '20px', fontSize: '20px'}}>{list.collectionListName}</div>
                                                <div style={{height: '20px', fontSize: '10px'}}>{list.musicVOList.length}首</div>
                                            </div>
                                        </div>
                                    </Link>
                                </Menu.Item>);
                            })}
                        </SubMenu>
                        
                    </Menu>
                </Sider>
                <Content style={{ padding: '0', minHeight: 280}}>
                    {type === 'rank' && <Table dataSource={rankList} columns={rankColomns} title={() => '听歌排行'} />}
                </Content>
            </Layout>
        )
    }
}
