import React, { Component } from 'react'
import { Layout, Menu, Image, Button, Table } from 'antd'
import {Link} from 'react-router-dom'
import axios from 'axios';
import { getToken, removeToken } from '../../utils/auth';
import { playMusic } from '../../utils/play';

const { Content, Sider } = Layout;
const {SubMenu} = Menu;
const rankColomns = [
    {
        key: 'index',
        render: (text, record, index) => {
            return index + 1;
        }
    },
    {
        dataIndex: 'musicName',
        key: 'musicName',
        title: '歌曲',
        render: (text, record, index) => {
            return (<Button type="link" onClick={() => {
                playMusic(record);
            }}>{text}</Button>);
        },
        width: '500px'
    },
    {
        dataIndex: 'artistName',
        key: 'artistName',
        title: '歌手',
        render: (text, record, index) => {
            return (<Link to={`/artist/${record.artistId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'albumName',
        key: 'albumName',
        title: '专辑',
        render: (text, record, index) => {
            return (<Link to={`/album/${record.albumId}`}>{text}</Link>);
        },
        width: '400px'
    },
    {
        dataIndex: 'userPlayTimes',
        key: 'userPlayTimes',
        title: '播放次数',
        render: (text) => {
            return text + '次';
        },
        width: '100px'
    }
];
export default class MyMusic extends Component {
    state = {collectionList: [], rankList: [], type: '', musicList: []}

    componentDidMount() {
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/user/music_rank',
            headers: {
                Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
            } else if (response.data.status === 1) {
                this.setState({type: 'rank', rankList: response.data.data});
            }
        });
        axios({
            method: 'GET',
            url: 'http://localhost:3001/server/collection_list',
            headers: {
                Authorization: getToken()
            }
        }).then((response) => {
            if (response.data.status === 2) {
                removeToken();
            } else if (response.data.status === 1) {
                this.setState({collectionList: response.data.data});
            }
        })
    }

    handleClick = (obj) => {
        if (obj.key === 'rank') {
            this.setState({type: 'rank'});
        } else {

        }
    }

    render() {
        const {rankList, collectionLists, type, collectionList} = this.state;
        return (
            <Layout>
                <Sider>
                    <Menu mode="inline" selectedKeys={type} style={{height: '100%'}} onClick={this.handleClick}>
                        <Menu.Item key="rank">听歌排行</Menu.Item>
                        <SubMenu key="collection_list" title="创建的歌单">
                            {this.state.collectionList.map((list) => {
                                return (<Menu.Item key={list.collectionListId} style={{paddingLeft: '10px', height: '50px'}}>
                                    <Link to={`/my_music/collection_list/${list.collectionListId}`}>
                                        <div style={{display: 'flex', height: '40px'}}>
                                            <Image width="40px" src={list.collectionListImg} preview={false} />
                                            <div style={{marginLeft: '10px', width: '100px', height: '40px', overflow: 'hidden'}}>{list.collectionListName}</div>
                                            <div style={{color: '#999', margin: 'auto 0 auto auto'}}>{list.musicVOList.length}首</div>
                                        </div>
                                    </Link>
                                </Menu.Item>);
                            })}
                        </SubMenu>
                        
                    </Menu>
                </Sider>
                <Content style={{ padding: '0', minHeight: 280}}>
                    {type === 'rank' && <Table dataSource={rankList} columns={rankColomns} title={() => '听歌排行'} />}
                    {type === 'collection_list' && 
                    <>
                    <div style={{display: 'flex', height: '200px'}}>
                        <img alt={collectionList.collectionListName} src={collectionList.collectionListImg} />
                        <Card title={collectionList.collectionListName} style={{overflow: 'scroll', overflowX: 'hidden'}}></Card>
                    </div>
                    <Content style={{ padding: '10px', minHeight: 280}}>
                        <Table columns={colomns} dataSource={collectionList.musicVOList} rowKey={record => record.collectionListId} />
                    </Content>
                    </>}
                </Content>
            </Layout>
        )
    }
}
