import React, { Component } from 'react'
import {StepBackwardOutlined, StepForwardOutlined, CaretRightOutlined, PauseOutlined, UnorderedListOutlined, SoundOutlined} from '@ant-design/icons';
import { Button, Dropdown, Menu } from 'antd'
import PubSub from 'pubsub-js';

export default class PlayBar extends Component {
    state = {
        // 播放列表
        playList: [],
        // 当前播放序号
        index: 0,
        paused: true,
        currentTime: 0,
        duration: 0
    }

    componentDidMount() {
        this.token = PubSub.subscribe('play', this.playMusic);
    }

    componentWillUnmount() {
        PubSub.unsubscribe(this.token);
    }

    // 收到通知，播放音乐，data为music对象
    playMusic = (msg, data) => {
        const playList = this.state.playList;
        for (let i = 0; i < playList.length; i++) {
            if (playList[i].musicId === data.musicId) {
                this.setState({index: i});
                return;
            }
        }
        const newPlayList = [...playList, data];
        this.setState({playList: newPlayList, index: newPlayList.length - 1});
    }

    playBackward = () => {
        const newIndex = (this.state.index - 1 + this.state.playList.length) % this.state.playList.length;
        this.setState({index: newIndex, paused: false});
        this.audio1.play();
    }
    
    playOrPause = () => {
        this.state.paused ? this.audio1.play() : this.audio1.pause();
        this.setState((state) => {
            return {paused: !state.paused};
        });
    }

    playForward = () => {
        const newIndex = (this.state.index + 1) % this.state.playList.length;
        this.setState({index: newIndex, paused: false});
        this.audio1.play();
    }

    // 音乐加载完毕，可以播放
    canPlay = () => {
        this.setState({duration: this.audio1.duration, paused: false});
        this.audio1.play();
    }

    // audio更新state时间
    updatePlayTime = () => {
        this.setState({currentTime: this.audio1.currentTime});
        if (this.audio1.currentTime === this.audio1.duration) {
            this.playForward();
        }
    }

    // 拖动条更新时间
    changePlayTime = (event) => {
        const {value} = event.target;
        this.audio1.currentTime = value;
    }

    // 解析时间成00:00格式, time以秒为单位
    parseTime = (time) => {
        var min = Math.floor(time / 60);
        if (min < 10) {
            min = '0' + min;
        }
        var sec = Math.floor(time % 60);
        if (sec < 10) {
            sec = '0' + sec;
        }
        return min + ':' + sec;
    }

    // 播放列表切换歌
    changeMusic = (obj) => {
        // console.log(obj.key);
        const index = this.state.playList.findIndex((music) => {
            return music.musicId === obj.key;
        });
        this.setState({index});
    }

    createMenu = () => {
        if (this.state.playList.length > 0) {
            return (
                <Menu selectedKeys={[this.state.playList[this.state.index].musicId]} onClick={this.changeMusic} style={{width: '950px', height: '300px', overflow: 'scroll', overflowX: 'hidden'}}>
                    <Menu.Item key="title" style={{width: '900px'}}>播放列表 ({this.state.playList.length})</Menu.Item>
                    <Menu.Divider />
                    {this.state.playList.map((music) => {
                        return (
                            <>
                                <Menu.Item key={music.musicId}>
                                    <div style={{display: 'flex'}}>
                                        <div style={{width: '500px'}}>{music.musicName}</div>
                                        <div style={{width: '300px'}}>{music.artistName}</div>
                                        <div style={{width: '100px'}}>{this.parseTime(music.timeLength / 1000)}</div>
                                    </div>
                                </Menu.Item>
                                <Menu.Divider />
                            </>
                        );
                    })}
                </Menu>
            );
        } else {
            return (
                <Menu>
                    <Menu.Item key="title" style={{width: '650px'}}>播放列表 ({this.state.playList.length})</Menu.Item>
                    <Menu.Divider />
                </Menu>
            );
        }
        
    }

    mute = () => {
        this.audio1.volume = 0;
    }

    render() {
        const {currentTime, duration} = this.state;
        return (
            <>
                <div style={{position: 'fixed', left: 0, bottom: 0, height: '60px', backgroundColor: 'black', width: '100%'}}>
                    <div style={{margin: '5px auto', width: '60%', display: 'flex'}} id="playBar">
                        <Button shape='circle' style={{backgroundColor: 'black', margin: 'auto 5px'}} onClick={this.playBackward}>
                            <StepBackwardOutlined style={{color: 'white', fontSize: '25px'}} />
                        </Button>
                        <Button shape='circle' style={{backgroundColor: 'black', margin: 'auto 5px'}} onClick={this.playOrPause} size="large">
                            {this.state.paused ? <CaretRightOutlined style={{color: 'white', fontSize: '35px', marginLeft: '3px', marginTop: '-4px'}} /> : <PauseOutlined style={{color: 'white', fontSize: '40px'}} />}
                        </Button>
                        <Button shape='circle' style={{backgroundColor: 'black', margin: 'auto 5px'}} onClick={this.playForward}>
                            <StepForwardOutlined style={{color: 'white', fontSize: '25px'}} />
                        </Button>
                        <img alt={this.state.playList.length > 0 ? this.state.playList[this.state.index].musicName : ''} src={this.state.playList.length > 0 ? this.state.playList[this.state.index].albumImg : '/favicon.ico'} style={{width: '50px', height: '50px', margin: '0 10px'}} />
                        <div style={{width: '550px'}}>
                            <div style={{color: 'white'}}>
                                {this.state.playList.length > 0 ? this.state.playList[this.state.index].musicName : '718音乐'}
                            </div>
                            <div className="barAndTime" style={{marginTop: '8px', width: '550px', display: 'flex'}}>
                                <div className="bar" style={{width: '450px', height: '10px', marginTop: '7px', display: 'flex'}}>
                                    <input type="range" max={duration} value={currentTime} style={{backgroundColor: 'red', height: '10px', width: '450px'}} onChange={this.changePlayTime} />
                                    {/* <Button shape="circle" icon={<FireOutlined />} type="primary" size="small" style={{marginLeft: '-12px', marginTop: '-6px'}} /> */}
                                </div>
                                <div className="time" style={{color: 'white', marginLeft: '5px'}}>{this.parseTime(currentTime)}/{this.parseTime(duration)}</div>
                            </div>
                        </div>
                        <Dropdown overlay={this.createMenu} placement="topCenter" arrow getPopupContainer={() => document.getElementById('playBar')}>
                            <Button style={{marginTop: '10px', border: '0'}} icon={<UnorderedListOutlined />} ghost></Button>
                        </Dropdown>
                        <div className="volumeCtrl" style={{marginLeft: '10px', display: 'flex'}}>
                            <Button icon={this.state.playList.length > 0 && this.audio1.volume === 0 ? <i className="fa fa-volume-off"></i> : <i className="fa fa-volume-up"></i>} style={{marginTop: '10px', border: '0'}} ghost onClick={this.mute} />
                            <input type="range" max="1" value="0.5" />
                        </div>
                    </div>
                </div>
                <audio src={this.state.playList.length > 0 ? this.state.playList[this.state.index].playUrl : ''} proload="auto" onCanPlay={this.canPlay} onTimeUpdate={this.updatePlayTime} ref={r => this.audio1 = r}></audio>
            </>
        )
    }
}
