import React, { Component } from 'react';
import { Button, Layout, Menu, Input } from 'antd';
import {Link, Route, Switch} from 'react-router-dom';
import 'antd/dist/antd.min.css';
import './App.css';
import ArtistCat from './pages/ArtistCat';
import Artist from './pages/Artist';
import PlayBar from './components/PlayBar';
import Album from './pages/Album';

const { Header, Content, Footer } = Layout;
const {Search} = Input;

export default class App extends Component {
    state = {login: false};

    render() {
        return (
            <>
                <Layout className="layout" style={{minHeight: '100vh'}}>
                    <Header>
                        <div style={{width: '1100px', margin: '0 auto', display: 'flex'}}>
                            <div className="logo" style={{color: 'white'}}>
                                <span className="fa fa-music"></span>&nbsp;
                                718音乐
                            </div>
                            <Menu theme="dark" mode="horizontal" defaultSelectedKeys="recommend" style={{marginLeft: 100}}>
                                <Menu.Item key="recommend">推荐</Menu.Item>
                                <Menu.Item key="ranking">排行榜</Menu.Item>
                                <Menu.Item key="artist">
                                    <Link to="/artist_list">歌手</Link>
                                </Menu.Item>
                                <Menu.Item key="my">我的音乐</Menu.Item>
                            </Menu>
                            <Search placeholder="input search text" style={{ width: '200px', display: 'block', margin: 'auto 20px auto auto'}} />
                            <Button type="primary" style={{display: 'block', margin: 'auto 20px'}}>登录</Button>
                        </div>
                    </Header>
                    <Content style={{ width: '1000px', margin: '0 auto' }}>
                        <div className="site-layout-content">
                            <Switch>
                                <Route path="/artist/:artistId" component={Artist} />
                                <Route path="/artist_list" component={ArtistCat} />
                                <Route path="/album/:albumId" component={Album} />
                            </Switch>
                        </div>
                    </Content>

                    <Footer style={{ textAlign: 'center', height: '150px' }}>718音乐 ©2021 Created by YWJ</Footer>
                </Layout>
                <PlayBar />
            </>
        );
    }
}
